INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161721, 'Fostamatinib', 'Rilpivirine', 'Moderate', 'Coadministration with fostamatinib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme and/or P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine and/or liver due to inhibition of CYP450 3A4-mediated metabolism and P-gp-mediated efflux by fostamatinib.', NULL, 'Caution is advised when fostamatinib is used concurrently with drugs that are known substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when fostamatinib is used concurrently with drugs that are known substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fostamatinib is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161894/', '[1] "Product Information. Tavalisse (fostamatinib)." Rigel Pharmaceuticals, South San Francisco, CA.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161722, 'Glasdegib', 'Rilpivirine', 'Moderate', 'Glasdegib may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of glasdegib with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'Coadministration of glasdegib with other drugs that can prolong the QT interval should generally be avoided. Caution and clinical monitoring are recommended if concomitant use is required. Patients should have electrocardiograms (ECGs) performed prior to initiation of treatment with glasdegib, approximately one week after initiation, and then once monthly for the next two months, although patients with risk factors may require more frequent and ongoing ECG monitoring.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161895/', '[1] "Product Information. Daurismo (glasdegib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] "Product Information. Daurismo (glasdegib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161723, 'Idelalisib', 'Rilpivirine', 'Moderate', 'Coadministration of idelalisib with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. The use of idelalisib has been associated with elevations in alanine aminotransferase (ALT) and aspartate aminotransferase (AST) greater than 5 times the upper limit of normal.', NULL, 'The use of idelalisib with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, 'The use of idelalisib with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; amiodarone; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; methotrexate; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; tetracyclines; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients treated with idelalisib should have serum ALT, AST, and bilirubin measured prior to initiation of treatment and regularly during treatment in accordance with the product labeling, and the dosing adjusted or interrupted as necessary.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161896/', '[1] "Product Information. Zydelig (idelalisib)." Gilead Sciences, Foster City, CA.[2] "Product Information. Hivid (zalcitabine)." Roche Laboratories, Nutley, NJ.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161724, 'Rilpivirine', 'Lomitapide', 'Major', 'Coadministration of lomitapide with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Lomitapide can cause elevations in serum transaminases and hepatic steatosis. Clinical data suggest that hepatic fat accumulation is reversible after stopping treatment with lomitapide, although the long-term consequences are unknown.', NULL, 'Caution is advised if lomitapide is used with other potentially hepatotoxic agents (e.', NULL, 'Caution is advised if lomitapide is used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; amiodarone; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; methotrexate; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; tamoxifen; tetracyclines; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; other lipid-lowering medications such as fenofibrate, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients treated with lomitapide should have serum ALT, AST, alkaline phosphatase, and total bilirubin measured prior to initiation of treatment and regularly during treatment in accordance with the product labeling, and the dosing adjusted or interrupted as necessary. Since alcohol may increase levels of hepatic fat and induce or exacerbate liver injury, the manufacturer recommends that patients taking lomitapide not consume more than one alcoholic drink per day. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161897/', '[1] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.[2] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.', 1767365208);
