INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161858, 'Aminoglutethimide', 'Rivaroxaban', 'Moderate', 'Coadministration with inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may decrease the plasma concentrations of rivaroxaban, which is a substrate of the isoenzyme as well as the efflux transporter.', NULL, 'Caution is advised if rivaroxaban is used in combination with inducers of CYP450 3A4 and/or P-glycoprotein.', NULL, 'Caution is advised if rivaroxaban is used in combination with inducers of CYP450 3A4 and/or P-glycoprotein. Pharmacologic effects of rivaroxaban should be monitored more closely whenever the inducer is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/162031/', '[1] "Product Information. Xarelto (rivaroxaban)." Bayer Inc, Toronto, IA.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161859, 'Amprenavir', 'Rivaroxaban', 'Moderate', 'Coadministration with moderate or potent inhibitors of CYP450 3A4 may increase the plasma concentrations of rivaroxaban, which is a substrate of the isoenzyme.', NULL, 'In patients with significant renal impairment, the use of rivaroxaban with moderate or potent inhibitors of CYP450 3A4 should be approached with caution.', NULL, 'In patients with significant renal impairment, the use of rivaroxaban with moderate or potent inhibitors of CYP450 3A4 should be approached with caution. Patients should be routinely evaluated for signs and symptoms suggesting blood loss such as a drop in hemoglobin and/or hematocrit, hypotension, or fetal distress (in pregnant women). Renal function should also be assessed periodically, and treatment with rivaroxaban discontinued if acute renal failure develops. Due to the lack of clinical data, rivaroxaban is not recommended in patients with CrCl below 30 mL/min when used for the prophylaxis of deep vein thrombosis and in patients with CrCl below 15 mL/min when used for reducing the risk of stroke and systemic embolism in nonvalvular atrial fibrillation.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/162032/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Xarelto (rivaroxaban)." Bayer Inc, Toronto, IA.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161860, 'Anisindione', 'Rivaroxaban', 'Major', 'Concomitant use of rivaroxaban with other anticoagulants may increase the risk of bleeding. In patients receiving neuraxial anesthesia or spinal puncture, the risk of developing an epidural or spinal hematoma during rivaroxaban therapy may also be increased by the concomitant use of other drugs that affect coagulation. The development of epidural and spinal hematoma can lead to long-term neurological injury or permanent paralysis. The safety of long-term coadministration of these drugs has not been studied.', NULL, 'Due to increased bleeding risk, the use of rivaroxaban with other anticoagulants should generally be avoided except during therapeutic transition periods where patients should be observed closely.', NULL, 'Due to increased bleeding risk, the use of rivaroxaban with other anticoagulants should generally be avoided except during therapeutic transition periods where patients should be observed closely. Patients should be routinely evaluated for signs and symptoms suggesting blood loss such as a drop in hemoglobin and/or hematocrit, hypotension, or fetal distress (in pregnant women). In patients undergoing neuraxial intervention, the use of rivaroxaban should be approached with caution and only after thorough assessment of risks and benefits. Besides bleeding complications, patients should also be monitored frequently for signs and symptoms of neurologic impairment such as midline back pain, sensory and motor deficits (numbness or weakness in lower limbs), and bowel or bladder dysfunction.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/162033/', '[1] "Product Information. Xarelto (rivaroxaban)." Bayer Inc, Toronto, IA.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161861, 'Anistreplase', 'Rivaroxaban', 'Major', 'Concomitant use of rivaroxaban with other agents that alter hemostasis such as aspirin, nonsteroidal anti-inflammatory drugs (NSAIDs), platelet aggregation inhibitors, other anticoagulants, thrombolytic agents, or drugs that cause thrombocytopenia may increase the risk of bleeding. In patients receiving neuraxial anesthesia or spinal puncture, the risk of developing an epidural or spinal hematoma during rivaroxaban therapy may also be increased by the concomitant use of other drugs that affect coagulation. The development of epidural and spinal hematoma can lead to long-term neurological injury or permanent paralysis.', NULL, 'Caution is recommended if rivaroxaban must be used with other agents that alter hemostasis.', NULL, 'Caution is recommended if rivaroxaban must be used with other agents that alter hemostasis. Patients should be monitored for increased anticoagulant effects and bleeding complications. In patients undergoing neuraxial intervention, coadministration of these agents should be approached with caution and only after thorough assessment of risks and benefits. Besides bleeding complications, patients should also be monitored frequently for signs and symptoms of neurologic impairment such as midline back pain, sensory and motor deficits (numbness or weakness in lower limbs), and bowel or bladder dysfunction.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/162034/', '[1] "Product Information. Xarelto (rivaroxaban)." Bayer Inc, Toronto, IA.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161862, 'Rivaroxaban', 'Ardeparin', 'Major', 'Drugs that can affect hemostasis such as dextran, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or other anticoagulants may potentiate the risk of bleeding complications associated with the use of a low molecular weight heparin (LMWH), heparinoid, or fondaparinux. In patients receiving neuraxial anesthesia or spinal puncture, the risk of developing an epidural or spinal hematoma during LMWH, heparinoid, or fondaparinux therapy may also be increased by the concomitant use of other drugs that affect coagulation. The development of epidural and spinal hematoma can lead to long-term or permanent paralysis.', NULL, 'In general, any agent that can enhance the risk of hemorrhage including other anticoagulants should be discontinued prior to initiation of LMWH, heparinoid, or fondaparinux therapy.', NULL, 'In general, any agent that can enhance the risk of hemorrhage including other anticoagulants should be discontinued prior to initiation of LMWH, heparinoid, or fondaparinux therapy. If coadministration is necessary, it should be undertaken with caution and only after thorough assessment of risks and benefits. Close clinical and laboratory observation for bleeding complications is recommended. Patients undergoing neuraxial intervention and treated with these agents should also be monitored frequently for signs and symptoms of neurologic impairment such as midline back pain, sensory and motor deficits (numbness or weakness in lower limbs), and bowel or bladder dysfunction. The optimal timing between the administration of anticoagulants and neuraxial procedures is not known.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/162035/', '[1] "Product Information. Fragmin (dalteparin)." Pharmacia and Upjohn, Kalamazoo, MI.[2] "Product Information. Normiflo (ardeparin)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[3] "Product Information. Lovenox (enoxaparin)." Rhone-Poulenc Rorer, Collegeville, PA.[4] "Product Information. Innohep (tinzaparin)" DuPont Pharmaceuticals, Wilmington, DE.[5] "Product Information. Arixtra (fondaparinux)." Organon, West Orange, NJ.[6] "Product Information. Orgaran (danaparoid)." Organon, West Orange, NJ.[7] Price AJ, Frcpath DO "Is there a clinical interaction between low molecular weight heparin and non-steroidal analgesics after total hip replacement?" Ann R Coll Surg Engl 77 (1995): 395', 1767365208);
