INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161865, 'Bismuth subsalicylate', 'Rivaroxaban', 'Moderate', 'Theoretically, salicylates may potentiate the effects of anticoagulants and increase the risk of bleeding. Salicylates interfere with the action of vitamin K and induce a dose-dependent alteration in hepatic synthesis of coagulation factors VII, IX and X, occasionally increasing the prothrombin time. While these effects are generally slight for most salicylates (except aspirin) at recommended dosages, they may be of clinical significance when combined with the inhibitory effects of anticoagulants on the clotting cascade. Moreover, salicylates are known to cause dose-related gastrointestinal bleeding, which may be complicated by anticoagulant therapy.', NULL, 'Until further data are available, products containing salicylates, especially if given chronically or in high dosages, should preferably be avoided in patients receiving anticoagulants.', NULL, 'Until further data are available, products containing salicylates, especially if given chronically or in high dosages, should preferably be avoided in patients receiving anticoagulants. Close clinical and laboratory observation for bleeding complications is recommended if concurrent therapy is necessary. The same precaution should be observed with the use of salicylate-related agents such as salicylamide because of their structural and pharmacological similarities.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/162038/', '[1] Wells PS, Holbrook AM, Crowther NR, Hirsh J "Interactions of warfarin with drugs and food." Ann Intern Med 121 (1994): 676-83[2] Fausa O "Salicylate-induced hypoprothrombinemia: a report of four cases." Acta Med Scand 188 (1970): 403-8[3] Koch-Weser J, Sellers EM "Drug interactions with coumarin anticoagulants (first of two parts)." N Engl J Med 285 (1971): 487-98[4] Koch-Weser J, Sellers EM "Drug interactions with coumarin anticoagulants (second of two parts)." N Engl J Med 285 (1971): 547-58[5] American Medical Association, Division of Drugs and Toxicology "Drug evaluations annual 1994." Chicago, IL: American Medical Association; (1994):[6] Barrow MV, Quick DT, Cunningham RW "Salicylate hypoprothrombinemia in rheumatoid arthritis with liver disease. Report of two cases." Arch Intern Med 120 (1967): 620-4[7] Weiss HJ, Aledort LM, Kochwa S "The effect of salicylates on the hemostatic properties of platelets in man." J Clin Invest 47 (1968): 2169-80[8] Penning-van Beest F, Erkens J, Petersen KU, Koelz HR, Herings R "Main comedications associated with major bleeding during anticoagulant therapy with coumarins." Eur J Clin Pharmacol 61 (2005): 439-44', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161866, 'Boceprevir', 'Rivaroxaban', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 that can also inhibit P-glycoprotein (P-gp) may significantly increase the plasma concentrations of rivaroxaban, which is a substrate of both the isoenzyme and efflux transporter. The risk of bleeding associated with rivaroxaban may be increased.', NULL, 'Concomitant use of rivaroxaban with dual P-gp and potent CYP450 3A4 inhibitors should generally be avoided.', NULL, 'Concomitant use of rivaroxaban with dual P-gp and potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of rivaroxaban during and for 2 weeks after treatment with itraconazole.', 'Metabolism, Absorption', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/162039/', '[1] "Product Information. Xarelto (rivaroxaban)." Bayer Inc, Toronto, IA.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[4] "Product Information. Evotaz (atazanavir-cobicistat)." Bristol-Myers Squibb, Princeton, NJ.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[7] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161867, 'Bromfenac', 'Rivaroxaban', 'Major', 'Concomitant use of rivaroxaban with other agents that alter hemostasis such as aspirin, nonsteroidal anti-inflammatory drugs (NSAIDs), platelet aggregation inhibitors, other anticoagulants, thrombolytic agents, or drugs that cause thrombocytopenia may increase the risk of bleeding. In patients receiving neuraxial anesthesia or spinal puncture, the risk of developing an epidural or spinal hematoma during rivaroxaban therapy may also be increased by the concomitant use of other drugs that affect coagulation. The development of epidural and spinal hematoma can lead to long-term neurological injury or permanent paralysis.', NULL, 'Caution is recommended if rivaroxaban must be used with other agents that alter hemostasis.', NULL, 'Caution is recommended if rivaroxaban must be used with other agents that alter hemostasis. Patients should be monitored for increased anticoagulant effects and bleeding complications. In patients undergoing neuraxial intervention, coadministration of these agents should be approached with caution and only after thorough assessment of risks and benefits. Besides bleeding complications, patients should also be monitored frequently for signs and symptoms of neurologic impairment such as midline back pain, sensory and motor deficits (numbness or weakness in lower limbs), and bowel or bladder dysfunction.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/162040/', '[1] "Product Information. Xarelto (rivaroxaban)." Bayer Inc, Toronto, IA.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161868, 'Butabarbital', 'Rivaroxaban', 'Moderate', 'Coadministration with inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may decrease the plasma concentrations of rivaroxaban, which is a substrate of the isoenzyme as well as the efflux transporter.', NULL, 'Caution is advised if rivaroxaban is used in combination with inducers of CYP450 3A4 and/or P-glycoprotein.', NULL, 'Caution is advised if rivaroxaban is used in combination with inducers of CYP450 3A4 and/or P-glycoprotein. Pharmacologic effects of rivaroxaban should be monitored more closely whenever the inducer is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/162041/', '[1] "Product Information. Xarelto (rivaroxaban)." Bayer Inc, Toronto, IA.', 1767365208);
