INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1420597, 'Ivacaftor', 'Dapsone', 'Moderate', 'Coadministration with ivacaftor may increase the plasma concentrations of drugs that are substrates of the CYP450 2C9 isoenzyme. The proposed mechanism, based on in vitro data, involves decreased metabolic clearance due to inhibition of CYP450 2C9 by ivacaftor.', 'DDInter', 1767317476, 'Caution is advised when ivacaftor is used concurrently with drugs that are known CYP450 2C9 substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ivacaftor is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when ivacaftor is used concurrently with drugs that are known CYP450 2C9 substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1420598, 'Ivacaftor', 'Daunorubicin (liposomal)', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of daunorubicin and idarubicin, both of which are substrates of the efflux transporter also known as ABCB1 or MDR1. The interaction has been studied with cyclosporine, a potent P-gp inhibitor, in attempt to overcome multidrug resistance in MDR1-overexpressing tumors.', 'DDInter', 1767317476, 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor. Patients should be closely monitored for increased adverse effects including cardiotoxicity and myelosuppression.', 'Absorption', 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor.', NULL, NULL, 'Moderate', 'pharmacodynamic');
