INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1420640, 'Ivacaftor', 'Fluvastatin', 'Moderate', 'Coadministration with inhibitors of CYP450 2C9 may increase the plasma concentrations of fluvastatin. According to the prescribing information, fluvastatin is primarily metabolized by CYP450 2C9 (approximately 75%) and, to a much lesser extent, by CYP450 2C8 and 3A4 (approximately 5% and 20%, respectively).', 'DDInter', 1767317476, 'Caution is advised when fluvastatin is prescribed with CYP450 2C9 inhibitors. The lowest starting dosage of fluvastatin is recommended, then titrated as needed based on clinical response and tolerance. Alternatively, pravastatin is not metabolized by CYP450 2C9 and may be a reasonable substitute for fluvastatin. All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', 'Caution is advised when fluvastatin is prescribed with CYP450 2C9 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1420641, 'Ivacaftor', 'Fluvoxamine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of ivacaftor, which is primarily metabolized by the isoenzyme. The interaction occurs to a greater extent when ivacaftor is administered as monotherapy than when administered in combination with lumacaftor, a potent CYP450 3A4 inducer.', 'DDInter', 1767317476, 'Caution is advised if ivacaftor is used with CYP450 3A4 inhibitors. A dosage adjustment for ivacaftor may be required if undue adverse effects occur.', 'Metabolism', 'Caution is advised if ivacaftor is used with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
