INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1420687, 'Ivacaftor', 'Letermovir', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ivacaftor, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Please consult manufacturer''s product labeling for complete dosing information. For ivacaftor - The frequency of dosing should be reduced to 1 tablet or packet once a day when coadministered with moderate CYP450 3A4 inhibitors. Patients should continue to receive the same tablet or oral granule packet strength, but instead of dosing twice a day, the frequency should be reduced to once a day.', 'Metabolism', 'Please consult manufacturer''s product labeling for complete dosing information.', NULL, NULL, 'Major', 'pharmacodynamic');
