INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1420729, 'Ivacaftor', 'Nefazodone', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ivacaftor, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'For ivacaftor -The ivacaftor dose should be reduced from 1 tablet or 1 packet of oral granules twice a day to 1 tablet or 1 packet of oral granules twice a week (i.e., every 3 to 4 days) during coadministration with potent CYP450 3A4 inhibitors. For example, a patient taking ivacaftor 150 mg tablet twice a day should reduce their ivacaftor dose to a 150 mg tablet twice a week and a patient taking ivacaftor 50 mg oral granule packet twice a day should reduce their ivacaftor dose to 50 mg oral granule packet twice a week, etc.', 'Metabolism', 'For ivacaftor -The ivacaftor dose should be reduced from 1 tablet or 1 packet of oral granules twice a day to 1 tablet or 1 packet of oral granules twice a week (i.', NULL, NULL, 'Major', 'pharmacodynamic');
