INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1420818, 'Ivacaftor', 'Tazemetostat', 'Moderate', 'Coadministration with CYP450 3A4 inhibitors may increase the plasma concentrations of tazemetostat, which is primarily metabolized by the isoenzyme. Clinically, this interaction may result in an increased risk of the frequency or severity of adverse reactions due to tazemetostat such as hemorrhage, pleural effusion, skin infection, dyspnea, pain, and respiratory distress. Data are not available for other, less potent inhibitors.', 'DDInter', 1767317476, 'Caution and clinical monitoring is recommended with the concomitant use of tazemetostat with CYP450 3A4 inhibitors. Patients should be advised to notify their health care professional if they experience signs or symptoms of possible tazemetostat-related adverse effects including secondary malignancies, hemorrhage, pleural effusion, skin infection, dyspnea, pain, and respiratory distress.', 'Metabolism', 'Caution and clinical monitoring is recommended with the concomitant use of tazemetostat with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
