INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1420841, 'Elexacaftor', 'Valsartan', 'Moderate', 'Coadministration with elexacaftor may increase the plasma concentrations of drugs that are substrates of the organic anion transporting polypeptides (OATP) 1B1 and 1B3. The proposed mechanism, based on in vitro data, involves decreased metabolic clearance due to inhibition of OATP 1B1- and 1B3-mediated hepatic uptake by elexacaftor and its metabolite. The clinical relevance is unknown.', 'DDInter', 1767317476, 'Caution is advised when elexacaftor is used concurrently with drugs that are known OATP 1B1 and 1B3 substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever elexacaftor is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when elexacaftor is used concurrently with drugs that are known OATP 1B1 and 1B3 substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1420842, 'Ivacaftor', 'Vardenafil', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or 2C9 may increase the plasma concentrations of vardenafil, which is primarily metabolized by the former and secondarily by the latter. The possibility of prolonged and/or increased pharmacologic effects of vardenafil should be considered.', 'DDInter', 1767317476, 'Dosage adjustments may be appropriate for vardenafil whenever a CYP450 3A4 and/or 2C9 inhibitor is added to or withdrawn from therapy based on efficacy and patient tolerance. The manufacturer recommends that an initial vardenafil dosage of 2.5 to 5 mg be used in patients treated concomitantly with a potent CYP450 3A4 inhibitor such as erythromycin, itraconazole, ketoconazole, indinavir, and ritonavir.', 'Metabolism', 'Dosage adjustments may be appropriate for vardenafil whenever a CYP450 3A4 and/or 2C9 inhibitor is added to or withdrawn from therapy based on efficacy and patient tolerance.', NULL, NULL, 'Moderate', 'pharmacodynamic');
