INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1420885, 'Fidaxomicin', 'Eliglustat', 'Minor', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of fidaxomicin and its main metabolite, OP-1118, both of which are substrates of the intestinal efflux transporter. Theoretically, concentrations of fidaxomicin and OP-1118 may also be decreased at the site of action (i.e., gastrointestinal tract) due to P-gp inhibition.', 'DDInter', 1767317476, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
