INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1420936, 'Eliglustat', 'Mirabegron', 'Major', 'Coadministration with inhibitors of CYP450 2D6 may significantly increase the plasma concentrations of eliglustat, which is primarily metabolized by CYP450 2D6 and, to a lesser extent, CYP450 3A4. Eliglustat at substantially elevated plasma concentrations is predicted to cause prolongation of the PR, QTc and QRS cardiac intervals, which may increase the risk of bradycardia, atrioventricular block, cardiac arrest, and serious ventricular arrhythmias such as torsade de pointes.', 'DDInter', 1767317476, 'The use of eliglustat in combination with one or more drugs that may result in moderate or potent inhibition of both CYP450 2D6 and 3A4 is considered contraindicated in CYP450 2D6 intermediate metabolizers (IMs) and extensive metabolizers (EMs). In the absence of a concomitant CYP450 3A4 inhibitor, eliglustat may be prescribed at a reduced dosage of 84 mg once daily to IMs and EMs treated with a potent or moderate CYP450 2D6 inhibitor. Poor metabolizers are not affected by CYP450 2D6 inhibition (since they already have minimal functional levels of the isoenzyme) and may also receive the reduced dosage of eliglustat, so long as they are not treated with a CYP450 3A4 inhibitor.', 'Metabolism', 'The use of eliglustat in combination with one or more drugs that may result in moderate or potent inhibition of both CYP450 2D6 and 3A4 is considered contraindicated in CYP450 2D6 intermediate metabolizers (IMs) and extensive metabolizers (EMs).', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1420937, 'Eliglustat', 'Mirtazapine', 'Moderate', 'Coadministration of mirtazapine with drugs that inhibit one or more of its metabolic pathways may result in increased plasma concentrations of mirtazapine. In vitro data from human liver microsomes indicate that CYP450 2D6 and 1A2 are involved in the formation of the 8-hydroxy metabolite, while CYP450 3A4 is primarily responsible for the formation of the N-desmethyl and N-oxide metabolites.', 'DDInter', 1767317476, 'The possibility of prolonged and/or increased pharmacologic effects of mirtazapine should be considered during coadministration of drugs that inhibit CYP450 1A2, 2D6 and/or 3A4. Therapeutic response to mirtazapine should be monitored more closely following initiation, discontinuation, or dosing change of CYP450 inhibitors, and the mirtazapine dosage adjusted as necessary.', 'Metabolism', 'The possibility of prolonged and/or increased pharmacologic effects of mirtazapine should be considered during coadministration of drugs that inhibit CYP450 1A2, 2D6 and/or 3A4.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1420938, 'Eliglustat', 'Mitotane', 'Major', 'Coadministration with potent inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may significantly decrease the plasma concentrations of eliglustat, which is primarily metabolized by CYP450 2D6 and, to a lesser extent, CYP450 3A4. Eliglustat is also a substrate of P-gp efflux transporter.', 'DDInter', 1767317476, 'Concomitant use of eliglustat with potent CYP450 3A4 or P-gp inducers should generally be avoided due to the potential for significantly reduced efficacy.', 'Metabolism', 'Concomitant use of eliglustat with potent CYP450 3A4 or P-gp inducers should generally be avoided due to the potential for significantly reduced efficacy.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1420939, 'Eliglustat', 'Mitoxantrone', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of mitoxantrone, which is a substrate of the efflux transporter also known as ABCB1 or MDR1. The interaction has been studied with cyclosporine, a potent P-gp inhibitor.', 'DDInter', 1767317476, 'Caution is advised if mitoxantrone is prescribed in combination with a P-gp inhibitor. Patients should be closely monitored for increased adverse effects including cardiotoxicity and myelosuppression.', 'Others', 'Caution is advised if mitoxantrone is prescribed in combination with a P-gp inhibitor.', NULL, NULL, 'Moderate', 'pharmacodynamic');
