INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1420952, 'Eliglustat', 'Paclitaxel (protein-bound)', 'Moderate', 'Coadministration with eliglustat may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine, liver, and/or kidney due to inhibition of P-glycoprotein-mediated drug efflux by eliglustat.', 'DDInter', 1767317476, 'Caution is advised when eliglustat is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever eliglustat is added to or withdrawn from therapy.', 'Others', 'Caution is advised when eliglustat is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1420953, 'Eliglustat', 'Palbociclib', 'Moderate', 'Coadministration with weak inhibitors of CYP450 3A4 may increase the plasma concentrations of eliglustat, which is primarily metabolized by CYP450 2D6 and, to a lesser extent, CYP450 3A4. Eliglustat at substantially elevated plasma concentrations is predicted to cause prolongation of the PR, QTc and QRS cardiac intervals, which may increase the risk of bradycardia, atrioventricular block, cardiac arrest, and serious ventricular arrhythmias such as torsade de pointes.', 'DDInter', 1767317476, 'Concomitant use of eliglustat with weak CYP450 3A4 inhibitors such as chloramphenicol, cyclosporine, danazol, dasatinib, ethinyl estradiol, fluvoxamine, goldenseal, isoniazid, ivacaftor, lapatinib, lomitapide, nifedipine, nilotinib, palbociclib, pazopanib, suvorexant, ticagrelor, and zafirlukast is not recommended in CYP450 2D6 poor metabolizers. No dosage adjustment for eliglustat is necessary when used with weak CYP450 3A4 inhibitors in extensive or intermediate metabolizers.', 'Metabolism', 'Concomitant use of eliglustat with weak CYP450 3A4 inhibitors such as chloramphenicol, cyclosporine, danazol, dasatinib, ethinyl estradiol, fluvoxamine, goldenseal, isoniazid, ivacaftor, lapatinib, lomitapide, nifedipine, nilotinib, palbociclib, pazopanib, suvorexant, ticagrelor, and zafirlukast is not recommended in CYP450 2D6 poor metabolizers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1420954, 'Eliglustat', 'Panobinostat', 'Major', 'Coadministration with inhibitors of CYP450 2D6 may significantly increase the plasma concentrations of eliglustat, which is primarily metabolized by CYP450 2D6 and, to a lesser extent, CYP450 3A4. Eliglustat at substantially elevated plasma concentrations is predicted to cause prolongation of the PR, QTc and QRS cardiac intervals, which may increase the risk of bradycardia, atrioventricular block, cardiac arrest, and serious ventricular arrhythmias such as torsade de pointes.', 'DDInter', 1767317476, 'The use of eliglustat in combination with one or more drugs that may result in moderate or potent inhibition of both CYP450 2D6 and 3A4 is considered contraindicated in CYP450 2D6 intermediate metabolizers (IMs) and extensive metabolizers (EMs). In the absence of a concomitant CYP450 3A4 inhibitor, eliglustat may be prescribed at a reduced dosage of 84 mg once daily to IMs and EMs treated with a potent or moderate CYP450 2D6 inhibitor. Poor metabolizers are not affected by CYP450 2D6 inhibition (since they already have minimal functional levels of the isoenzyme) and may also receive the reduced dosage of eliglustat, so long as they are not treated with a CYP450 3A4 inhibitor.', 'Metabolism', 'The use of eliglustat in combination with one or more drugs that may result in moderate or potent inhibition of both CYP450 2D6 and 3A4 is considered contraindicated in CYP450 2D6 intermediate metabolizers (IMs) and extensive metabolizers (EMs).', NULL, NULL, 'Major', 'pharmacodynamic');
