INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421005, 'Eliglustat', 'Talazoparib', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may increase the plasma concentrations of talazoparib, which has been shown in vitro to be a substrate of both efflux membrane transporters.', 'DDInter', 1767317476, 'No initial dosage adjustment is recommended by the manufacturer when talazoparib is coadministered with inhibitors of BCRP and/or P-gp other than amiodarone, carvedilol, clarithromycin, itraconazole, or verapamil. However, patients should be closely monitored for adverse effects such as myelosuppression and myelodysplastic syndrome/acute myeloid leukemia, and dosage adjustments made or treatment withheld as needed in accordance with the product labeling.', 'Distribution', 'No initial dosage adjustment is recommended by the manufacturer when talazoparib is coadministered with inhibitors of BCRP and/or P-gp other than amiodarone, carvedilol, clarithromycin, itraconazole, or verapamil.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421006, 'Eliglustat', 'Tamoxifen', 'Major', 'Chronic coadministration of potent or moderate CYP450 2D6 inhibitors including certain antidepressants may reduce the effectiveness of tamoxifen. The proposed mechanism is inhibition of tamoxifen bioactivation via CYP450 2D6 to endoxifen (4-hydroxy-N-desmethyltamoxifen), the active metabolite that may be responsible for much of tamoxifen''s antiestrogenic activity.', 'DDInter', 1767317476, 'Based on available data, patients treated with tamoxifen should avoid the chronic use of potent CYP450 2D6 inhibitors such as fluoxetine, paroxetine, and quinidine whenever possible, and preferably also moderate inhibitors such as bupropion, duloxetine, and sertraline. If an antidepressant is required during treatment with tamoxifen, agents such as desvenlafaxine, fluvoxamine, milnacipran, levomilnacipran, mirtazapine, and venlafaxine may be considered, since they have mild to no effects on CYP450 2D6. Alternatively, aromatase inhibitors such as anastrozole, exemestane, and letrozole may be appropriate substitutes for tamoxifen in certain patients. Alternatively, aromatase inhibitors such as anastrozole, exemestane, and letrozole may be appropriate substitutes for tamoxifen in certain patients.', 'Metabolism', 'Based on available data, patients treated with tamoxifen should avoid the chronic use of potent CYP450 2D6 inhibitors such as fluoxetine, paroxetine, and quinidine whenever possible, and preferably also moderate inhibitors such as bupropion, duloxetine, and sertraline.', NULL, NULL, 'Major', 'pharmacodynamic');
