INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421013, 'Eliglustat', 'Tetrabenazine', 'Moderate', 'Coadministration with eliglustat may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 2D6.', 'DDInter', 1767317476, 'Caution is advised if eliglustat is used concomitantly with medications that undergo metabolism primarily by CYP450 2D6, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever eliglustat is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if eliglustat is used concomitantly with medications that undergo metabolism primarily by CYP450 2D6, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421014, 'Eliglustat', 'Thioridazine', 'Major', 'Coadministration with inhibitors of CYP450 2D6 may increase the plasma concentrations of thioridazine and its two active metabolites, mesoridazine and sulforidazine, all of which are substrates of the isoenzyme.', 'DDInter', 1767317476, 'The use of thioridazine with drugs that inhibit CYP450 2D6 is considered contraindicated. Depending on the elimination half-life of these drugs, a considerable waiting period may be appropriate following their discontinuation before thioridazine is initiated. For example, the manufacturer of fluoxetine recommends that thioridazine not be administered within 5 weeks after discontinuing fluoxetine because of the drug''s long half-life.', 'Metabolism', 'The use of thioridazine with drugs that inhibit CYP450 2D6 is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421015, 'Eliglustat', 'Ticagrelor', 'Moderate', 'Coadministration with weak inhibitors of CYP450 3A4 may increase the plasma concentrations of eliglustat, which is primarily metabolized by CYP450 2D6 and, to a lesser extent, CYP450 3A4. Eliglustat at substantially elevated plasma concentrations is predicted to cause prolongation of the PR, QTc and QRS cardiac intervals, which may increase the risk of bradycardia, atrioventricular block, cardiac arrest, and serious ventricular arrhythmias such as torsade de pointes.', 'DDInter', 1767317476, 'Concomitant use of eliglustat with weak CYP450 3A4 inhibitors such as chloramphenicol, cyclosporine, danazol, dasatinib, ethinyl estradiol, fluvoxamine, goldenseal, isoniazid, ivacaftor, lapatinib, lomitapide, nifedipine, nilotinib, palbociclib, pazopanib, suvorexant, ticagrelor, and zafirlukast is not recommended in CYP450 2D6 poor metabolizers. No dosage adjustment for eliglustat is necessary when used with weak CYP450 3A4 inhibitors in extensive or intermediate metabolizers.', 'Metabolism', 'Concomitant use of eliglustat with weak CYP450 3A4 inhibitors such as chloramphenicol, cyclosporine, danazol, dasatinib, ethinyl estradiol, fluvoxamine, goldenseal, isoniazid, ivacaftor, lapatinib, lomitapide, nifedipine, nilotinib, palbociclib, pazopanib, suvorexant, ticagrelor, and zafirlukast is not recommended in CYP450 2D6 poor metabolizers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
