INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421035, 'Eliglustat', 'Voxelotor', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of eliglustat, which is primarily metabolized by CYP450 2D6 and, to a lesser extent, CYP450 3A4. Eliglustat at substantially elevated plasma concentrations is predicted to cause prolongation of the PR, QTc and QRS cardiac intervals, which may increase the risk of bradycardia, atrioventricular block, cardiac arrest, and serious ventricular arrhythmias such as torsade de pointes.', 'DDInter', 1767317476, 'The use of eliglustat in combination with one or more drugs that may result in moderate inhibition of CYP450 3A4 and moderate to potent inhibition of CYP450 2D6 is considered contraindicated in CYP450 2D6 extensive metabolizers (EMs) and intermediate metabolizers (IMs). In the absence of a concomitant CYP450 2D6 inhibitor, eliglustat may be prescribed at a reduced dosage of 84 mg once daily to EMs treated with a moderate CYP450 3A4 inhibitor. However, eliglustat should not be used with a moderate CYP450 3A4 inhibitor in CYP450 2D6 IMs or poor metabolizers (PMs). Moderate CYP450 3A4 inhibitors include aprepitant, ciprofloxacin, clotrimazole, crizotinib, darunavir, diltiazem, dronedarone, fluconazole, fusidic acid, imatinib, isavuconazonium, miconazole, mifepristone, netupitant, quinupristin-dalfopristin, ranolazine, stiripentol, and verapamil. Potent and moderate CYP450 2D6 inhibitors include abiraterone, bupropion, celecoxib, cimetidine, cinacalcet, clobazam, darifenacin, diphenhydramine, duloxetine, fluoxetine, methotrimeprazine, mirabegron, paroxetine, propoxyphene, quinidine, ranolazine, sertraline, stiripentol, and terbinafine.', 'Metabolism', 'The use of eliglustat in combination with one or more drugs that may result in moderate inhibition of CYP450 3A4 and moderate to potent inhibition of CYP450 2D6 is considered contraindicated in CYP450 2D6 extensive metabolizers (EMs) and intermediate metabolizers (IMs).', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421036, 'Eliglustat', 'Zafirlukast', 'Moderate', 'Coadministration with weak inhibitors of CYP450 3A4 may increase the plasma concentrations of eliglustat, which is primarily metabolized by CYP450 2D6 and, to a lesser extent, CYP450 3A4. Eliglustat at substantially elevated plasma concentrations is predicted to cause prolongation of the PR, QTc and QRS cardiac intervals, which may increase the risk of bradycardia, atrioventricular block, cardiac arrest, and serious ventricular arrhythmias such as torsade de pointes.', 'DDInter', 1767317476, 'Concomitant use of eliglustat with weak CYP450 3A4 inhibitors such as chloramphenicol, cyclosporine, danazol, dasatinib, ethinyl estradiol, fluvoxamine, goldenseal, isoniazid, ivacaftor, lapatinib, lomitapide, nifedipine, nilotinib, palbociclib, pazopanib, suvorexant, ticagrelor, and zafirlukast is not recommended in CYP450 2D6 poor metabolizers. No dosage adjustment for eliglustat is necessary when used with weak CYP450 3A4 inhibitors in extensive or intermediate metabolizers.', 'Metabolism', 'Concomitant use of eliglustat with weak CYP450 3A4 inhibitors such as chloramphenicol, cyclosporine, danazol, dasatinib, ethinyl estradiol, fluvoxamine, goldenseal, isoniazid, ivacaftor, lapatinib, lomitapide, nifedipine, nilotinib, palbociclib, pazopanib, suvorexant, ticagrelor, and zafirlukast is not recommended in CYP450 2D6 poor metabolizers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421037, 'Eliglustat', 'Zileuton', 'Moderate', 'Coadministration with weak inhibitors of CYP450 3A4 may increase the plasma concentrations of eliglustat, which is primarily metabolized by CYP450 2D6 and, to a lesser extent, CYP450 3A4. Eliglustat at substantially elevated plasma concentrations is predicted to cause prolongation of the PR, QTc and QRS cardiac intervals, which may increase the risk of bradycardia, atrioventricular block, cardiac arrest, and serious ventricular arrhythmias such as torsade de pointes.', 'DDInter', 1767317476, 'Concomitant use of eliglustat with weak CYP450 3A4 inhibitors such as chloramphenicol, cyclosporine, danazol, dasatinib, ethinyl estradiol, fluvoxamine, goldenseal, isoniazid, ivacaftor, lapatinib, lomitapide, nifedipine, nilotinib, palbociclib, pazopanib, suvorexant, ticagrelor, and zafirlukast is not recommended in CYP450 2D6 poor metabolizers. No dosage adjustment for eliglustat is necessary when used with weak CYP450 3A4 inhibitors in extensive or intermediate metabolizers.', 'Metabolism', 'Concomitant use of eliglustat with weak CYP450 3A4 inhibitors such as chloramphenicol, cyclosporine, danazol, dasatinib, ethinyl estradiol, fluvoxamine, goldenseal, isoniazid, ivacaftor, lapatinib, lomitapide, nifedipine, nilotinib, palbociclib, pazopanib, suvorexant, ticagrelor, and zafirlukast is not recommended in CYP450 2D6 poor metabolizers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421038, 'Aminoglutethimide', 'Theophylline', 'Moderate', 'Aminoglutethimide may significantly reduce theophylline serum concentrations by increasing theophylline clearance. The mechanism is induction of CYP450 3A4 hepatic microsomal enzymes.', 'DDInter', 1767317476, 'The patient should be monitored for clinical and laboratory evidence of altered theophylline response when aminoglutethimide is added, discontinued, or the dosage is changed. Patients should be advised to notify their physician if they experience a worsening of their respiratory symptoms during concurrent therapy.', 'Metabolism', 'The patient should be monitored for clinical and laboratory evidence of altered theophylline response when aminoglutethimide is added, discontinued, or the dosage is changed.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421039, 'Theophylline', 'Atracurium', 'Moderate', 'Methylxanthines may antagonize the effects of neuromuscular blockers. The mechanism is unknown. In patients stabilized on aminophylline, resistance to pancuronium has been reported.', 'DDInter', 1767317476, 'The patient should be monitored for adequate neuromuscular blockade and the dosage adjusted, if necessary.', 'Antagonism', 'The patient should be monitored for adequate neuromuscular blockade and the dosage adjusted, if necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421040, 'Butabarbital', 'Theophylline', 'Moderate', 'Barbiturates may decrease serum levels and therapeutic effects of the methylxanthines. The mechanism is barbiturate induction of CYP450 3A4 and 1A2 hepatic metabolism of methylxanthines.', 'DDInter', 1767317476, 'Close observation for clinical and laboratory evidence of decreased methylxanthine effect is indicated if these drugs must be used together. Patients should be advised to notify their physician if they experience a worsening of their respiratory symptoms.', 'Metabolism', 'Close observation for clinical and laboratory evidence of decreased methylxanthine effect is indicated if these drugs must be used together.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421041, 'Carteolol', 'Theophylline', 'Major', 'The pharmacologic effects of theophyllines and beta-blockers are opposite. Nonselective and high doses of cardioselective beta-blockers may cause severe or fatal bronchospasm by opposing theophylline-induced bronchodilation. Ophthalmic beta-blockers undergo significant systemic absorption and may also interact. In addition, propranolol and other beta-blockers may reduce the CYP450 hepatic metabolism of theophylline, and serum theophylline levels may be increased.', 'DDInter', 1767317476, 'Oral and ophthalmic nonselective beta-blockers (e.g., carteolol, carvedilol, levobunolol, metipranolol, nadolol, oxprenolol, penbutolol, pindolol, propranolol, sotalol, and timolol) are considered contraindicated in patients with bronchospastic diseases. Cardioselective beta-blockers should generally be avoided, or used with extreme caution if no other alternatives are available and the benefits outweigh the risks of potentially severe bronchospasm. If patients do receive this combination, they should be closely monitored for increased serum theophylline levels but decreased bronchodilatory effectiveness.', 'Antagonism', 'Oral and ophthalmic nonselective beta-blockers (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421042, 'Activated charcoal', 'Theophylline', 'Moderate', 'Charcoal may reduce the absorption of many drugs and can absorb enterohepatically circulated drugs. Clinical utility may be the reduction either of the effects or of the toxicity of many drugs. Activated charcoal may adsorb any therapeutic agents administered while it is in the gastrointestinal tract.', 'DDInter', 1767317476, 'The regular ingestion of charcoal should be avoided by patients requiring maintenance medications. If concomitant use is necessary, the dosage or route of administration may need to be altered.', 'Absorption', 'The regular ingestion of charcoal should be avoided by patients requiring maintenance medications.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421043, 'Cinoxacin', 'Theophylline', 'Moderate', 'Some fluoroquinolones have been shown to decrease the hepatic metabolism of theophyllines. The mechanism is inhibition of CYP450 1A2 hepatic metabolism. Patients with chronic obstructive pulmonary disease, congestive heart failure, or cirrhosis may have slower theophylline clearance rates; therefore, they may be at greater risk of developing theophylline toxicity. This drug combination may increase the risk of seizures, especially in the elderly. Interpatient variability of this effect makes it prudent to monitor patients who may not tolerate a small increase in theophylline levels during concurrent fluoroquinolone therapy.', 'DDInter', 1767317476, 'Interpatient variability of this effect makes it prudent to monitor patients who may not tolerate a small increase in theophylline levels. Levofloxacin, sparfloxacin, and lomefloxacin have been reported to cause minor or no changes in theophylline levels and may be considered as alternatives. Patients should be advised to report any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, seizures, or irregular heartbeat to their physicians.', 'Metabolism', 'Interpatient variability of this effect makes it prudent to monitor patients who may not tolerate a small increase in theophylline levels.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421044, 'Demeclocycline', 'Theophylline', 'Moderate', 'Coadministration of a tetracycline may decrease theophylline plasma clearance and increase theophylline levels. Data are available for minocycline, tetracycline, and doxycycline, but are conflicting in some cases. This interaction appears to be more problematic if a tetracycline is administered for more than five days. Patients with chronic obstructive pulmonary disease, congestive heart failure, or cirrhosis may have slower theophylline clearance rates; therefore, they may be at greater risk of developing theophylline toxicity.', 'DDInter', 1767317476, 'If these drugs are given concurrently, close clinical and laboratory monitoring of response and tolerance is recommended. Patients should be advised to notify their physician if they experience any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, seizures, or irregular heartbeats. It may be necessary to reduce theophylline dosage.', 'Metabolism', 'If these drugs are given concurrently, close clinical and laboratory monitoring of response and tolerance is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421045, 'Doxacurium', 'Theophylline', 'Moderate', 'Methylxanthines may antagonize the effects of neuromuscular blockers. The mechanism is unknown. In patients stabilized on aminophylline, resistance to pancuronium has been reported.', 'DDInter', 1767317476, 'The patient should be monitored for adequate neuromuscular blockade and the dosage adjusted, if necessary.', 'Antagonism', 'The patient should be monitored for adequate neuromuscular blockade and the dosage adjusted, if necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421046, 'Echinacea', 'Theophylline', 'Moderate', 'Coadministration with echinacea may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of CYP450 1A2. The proposed mechanism is decreased clearance due to echinacea-mediated inhibition of CYP450 1A2.', 'DDInter', 1767317476, 'Caution is advised if echinacea is used concomitantly with drugs that are substrates of CYP450 1A2, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever echinacea is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Caution is advised if echinacea is used concomitantly with drugs that are substrates of CYP450 1A2, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421047, 'Emapalumab', 'Theophylline', 'Moderate', 'Plasma concentrations of drugs that are CYP450 substrates may decrease following the initiation of interleukin (IL) inhibitors, tumor necrosis factor (TNF) blockers, or interferon (IFN) inhibitors in patients with chronic inflammatory diseases. Because the formation of hepatic CYP450 enzymes is down-regulated during infection and chronic inflammation by increased levels of certain cytokines (e.g., interleukins-1, -6, and -10; tumor necrosis factor alpha; interferons), treatment targeting these cytokines may restore or normalize CYP450 enzyme levels resulting in increased metabolism of these drugs.', 'DDInter', 1767317476, 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.g., antiarrhythmics, anticonvulsants, immunosuppressants, theophylline) or sensitive substrates where decreases in plasma levels may be significant or undesirable (e.g., oral contraceptives, statins, benzodiazepines, opioids). Clinical and/or laboratory monitoring should be considered following the initiation or withdrawal of such treatments, and the dosage(s) of the CYP450 substrate(s) adjusted accordingly. The effects of IL inhibitors, TNF blockers, and IFN inhibitors on CYP450 activities may persist for several weeks after stopping therapy.', 'Metabolism', 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421048, 'Enoxacin', 'Theophylline', 'Major', 'Enoxacin has been shown to reduce theophylline clearance by up to 75%. The mechanism is inhibition of CYP450 1A2 hepatic metabolism. Theophylline serum levels increase 1.5 to 3.0 times when enoxacin is administered concurrently. Patients with chronic obstructive pulmonary disease, congestive heart failure, or cirrhosis may have slower theophylline clearance rates; therefore, they may be at greater risk of developing theophylline toxicity.', 'DDInter', 1767317476, 'If possible, enoxacin therapy should be avoided in patients receiving theophylline. Levofloxacin, sparfloxacin, moxifloxacin, gatifloxacin, trovafloxacin, and lomefloxacin have been reported to cause minor or no changes in theophylline levels and may be considered as alternatives. If enoxacin is given concomitantly with theophylline, the theophylline dosage should be reduced approximately 50% or more, and theophylline serum levels should be closely monitored. Patients should be advised to report any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, seizures, or irregular heartbeats to their physician.', 'Metabolism', 'If possible, enoxacin therapy should be avoided in patients receiving theophylline.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421049, 'Ephedrine', 'Theophylline', 'Minor', 'Ephedrine-theophylline or ephedrine-dyphylline combinations are used for the treatment of asthma but the efficacy of the combination has been questioned. This combination may lead to increased xanthine side effects. The mechanism is unknown, but may be related to synergistic pharmacologic effects. Patients using this combination should be closely monitored for side effects such as nausea, vomiting, tachycardia, nervousness, or insomnia. If side effects are noted, the dosage of the xanthine may need to be decreased.', 'DDInter', 1767317476, '-', 'Synergism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421050, 'Ephedrine (nasal)', 'Theophylline', 'Minor', 'Ephedrine-theophylline or ephedrine-dyphylline combinations are used for the treatment of asthma but the efficacy of the combination has been questioned. This combination may lead to increased xanthine side effects. The mechanism is unknown, but may be related to synergistic pharmacologic effects. Patients using this combination should be closely monitored for side effects such as nausea, vomiting, tachycardia, nervousness, or insomnia. If side effects are noted, the dosage of the xanthine may need to be decreased.', 'DDInter', 1767317476, '-', 'Synergism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
