INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421118, 'Theophylline', 'Metreleptin', 'Moderate', 'Treatment with metreleptin, a recombinant analog of human leptin, may affect the pharmacokinetics of drugs that are primarily metabolized by CYP450 isoenzymes.', 'DDInter', 1767317476, 'Caution is advised when metreleptin is prescribed to patients receiving drugs with a narrow therapeutic index that are substrates of CYP450 isoenzymes, such as macrolide immunosuppressants, oral contraceptives, cyclosporine, theophylline, and warfarin. Clinical and laboratory monitoring are recommended following the initiation, discontinuation or change of dosage of metreleptin, and the individual dosage of the concomitant agents adjusted as needed.', 'Metabolism', 'Caution is advised when metreleptin is prescribed to patients receiving drugs with a narrow therapeutic index that are substrates of CYP450 isoenzymes, such as macrolide immunosuppressants, oral contraceptives, cyclosporine, theophylline, and warfarin.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421119, 'Theophylline', 'Mexiletine', 'Major', 'Coadministration with mexiletine may increase the serum concentrations of theophylline. The proposed mechanism is inhibition of the CYP450 1A2-mediated metabolism of theophylline by mexiletine.', 'DDInter', 1767317476, 'Caution is advised when theophylline is used with mexiletine. Close monitoring of serum theophylline levels is recommended, particularly following the addition, discontinuation or change of dosage of mexiletine, and the theophylline dosage adjusted accordingly. Patients should be advised to notify their physician if they experience signs of theophylline toxicity such as nausea, vomiting, diarrhea, headache, restlessness, insomnia, seizures, palpitations, and cardiac arrhythmia.', 'Metabolism', 'Caution is advised when theophylline is used with mexiletine.', NULL, NULL, 'Major', 'pharmacodynamic');
