INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421122, 'Theophylline', 'Moricizine', 'Minor', 'Moricizine may increase the plasma clearance of theophylline by 46% to 68% and decrease theophylline half-life by 20% to 34%. Conversely, theophylline toxicity may occur when moricizine is discontinued. The mechanism is unknown, but may be related to hepatic enzyme induction. Management consists of monitoring the patient for theophylline efficacy, checking levels if indicated, and adjusting theophylline dosage accordingly. Patients should be advised to notify their physician if they experience a worsening of their respiratory symptoms.', 'DDInter', 1767317476, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421123, 'Theophylline', 'Mycophenolate mofetil', 'Minor', 'In vitro studies have shown that mycophenolic acid in concentrations up to 100 mcg/mL decreases protein binding of theophylline from 53% to 45%. The clinical significance of this interaction is unknown. Decreased protein binding theoretically could lead to increased plasma theophylline concentrations. However, clinical toxicity has not been reported. It may be appropriate to monitor more closely patients receiving theophylline or other methylxanthine derivatives with mycophenolic acid products. Patients should be advised to contact their physician if they experience potential symptoms of theophylline toxicity such as nausea, vomiting, diarrhea, headache, restlessness, insomnia, and irregular heartbeat.', 'DDInter', 1767317476, '-', 'Distribution', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
