INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421126, 'Theophylline', 'Nalidixic acid', 'Moderate', 'Some fluoroquinolones have been shown to decrease the hepatic metabolism of theophyllines. The mechanism is inhibition of CYP450 1A2 hepatic metabolism. Patients with chronic obstructive pulmonary disease, congestive heart failure, or cirrhosis may have slower theophylline clearance rates; therefore, they may be at greater risk of developing theophylline toxicity. This drug combination may increase the risk of seizures, especially in the elderly. Interpatient variability of this effect makes it prudent to monitor patients who may not tolerate a small increase in theophylline levels during concurrent fluoroquinolone therapy.', 'DDInter', 1767317476, 'Interpatient variability of this effect makes it prudent to monitor patients who may not tolerate a small increase in theophylline levels. Levofloxacin, sparfloxacin, and lomefloxacin have been reported to cause minor or no changes in theophylline levels and may be considered as alternatives. Patients should be advised to report any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, seizures, or irregular heartbeat to their physicians.', 'Metabolism', 'Interpatient variability of this effect makes it prudent to monitor patients who may not tolerate a small increase in theophylline levels.', NULL, NULL, 'Moderate', 'pharmacodynamic');
