INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421128, 'Theophylline', 'Nefazodone', 'Moderate', 'Coadministration with nefazodone may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by nefazodone.', 'DDInter', 1767317476, 'Caution is advised if nefazodone must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. A lower initial dosage, as well as clinical and laboratory monitoring, may be appropriate for some drugs.', 'Metabolism', 'Caution is advised if nefazodone must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421129, 'Theophylline', 'Nelfinavir', 'Moderate', 'Coadministration with nelfinavir may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by nelfinavir.', 'DDInter', 1767317476, 'Caution is advised if nelfinavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever nelfinavir is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if nelfinavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421130, 'Theophylline', 'Nisoldipine', 'Minor', 'Limited and controversial data suggest the following: either there is no significant interaction between theophylline and dihydropyridine calcium channel blockers, or theophylline serum levels increase after the addition of dihydropyridine calcium channel blockers. Data are available for nifedipine. Theophylline dosage should be reduced if necessary, and plasma levels should be checked when clinically necessary and appropriate. Patients should be advised to report any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat to their physician.', 'DDInter', 1767317476, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421131, 'Theophylline', 'Nizatidine', 'Moderate', 'Cimetidine may increase theophylline plasma concentrations by as much as 70%. The mechanism is related to inhibition of liver CYP450 enzymes responsible for theophylline metabolism. Patients with chronic obstructive pulmonary disease, congestive heart failure, or cirrhosis may have slower theophylline clearance rates; therefore, they may be at greater risk of developing theophylline toxicity.', 'DDInter', 1767317476, 'Clinical monitoring of patient response, tolerance, and laboratory theophylline serum concentrations is recommended. Patients should be advised to report any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat to their physician.', 'Metabolism', 'Clinical monitoring of patient response, tolerance, and laboratory theophylline serum concentrations is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
