INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421150, 'Theophylline', 'Phenytoin', 'Moderate', 'Phenytoin has been reported to increase the clearance of theophylline by 40% to 50%, presumably by enhancing the hepatic CYP450 metabolism of theophylline. Also, coadministration of theophylline has been reported to lower serum phenytoin concentrations. Other hydantoins and hepatically metabolized methylxanthines may also interact.', 'DDInter', 1767317476, 'Management consists of monitoring clinical response and serum concentrations of both drugs and adjusting dosages as necessary. Patients should be advised to report loss of seizure control or worsening of respiratory symptoms to their physician.', 'Metabolism', 'Management consists of monitoring clinical response and serum concentrations of both drugs and adjusting dosages as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
