INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421173, 'Theophylline', 'Rofecoxib', 'Moderate', 'The coadministration with rofecoxib may increase the plasma concentrations of theophylline. The proposed mechanism is rofecoxib inhibition of CYP450 1A2, the isoenzyme primarily responsible for the metabolic clearance of theophylline.', 'DDInter', 1767317476, 'Caution is advised during coadministration of theophylline with rofecoxib. Theophylline plasma levels should be closely monitored and the dosage adjusted accordingly, particularly following initiation, discontinuation or change of dosage of rofecoxib therapy in patients who are stabilized on their theophylline regimen. Patients should be advised to notify their physician if they experience signs of theophylline toxicity such as nausea, vomiting, diarrhea, headache, restlessness, insomnia, seizures, palpitations, and cardiac arrhythmia.', 'Metabolism', 'Caution is advised during coadministration of theophylline with rofecoxib.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421174, 'Theophylline', 'Roflumilast', 'Minor', 'Coadministration with theophylline may increase the systemic exposure to roflumilast and its pharmacologically active N-oxide metabolite.', 'DDInter', 1767317476, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421175, 'Theophylline', 'Ropivacaine', 'Minor', 'Ropivacaine serum concentrations may increase when given with drugs metabolized by the cytochrome P-450 isoenzyme 1A2. The theorized mechanism is via competitive inhibition. Information is only available from in vitro studies. Theophylline and mexiletine, drugs metabolized by CYP450 1A2, may participate in this interaction.', 'DDInter', 1767317476, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421176, 'Theophylline', 'Rucaparib', 'Moderate', 'Coadministration with rucaparib may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of CYP450 1A2. The proposed mechanism is decreased clearance due to rucaparib-mediated inhibition of CYP450 1A2.', 'DDInter', 1767317476, 'Caution is advised if rucaparib is used concomitantly with drugs that are substrates of CYP450 1A2, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever rucaparib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Caution is advised if rucaparib is used concomitantly with drugs that are substrates of CYP450 1A2, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
