INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421188, 'Theophylline', 'Sotalol', 'Major', 'The pharmacologic effects of theophyllines and beta-blockers are opposite. Nonselective and high doses of cardioselective beta-blockers may cause severe or fatal bronchospasm by opposing theophylline-induced bronchodilation. Ophthalmic beta-blockers undergo significant systemic absorption and may also interact. In addition, propranolol and other beta-blockers may reduce the CYP450 hepatic metabolism of theophylline, and serum theophylline levels may be increased.', 'DDInter', 1767317476, 'Oral and ophthalmic nonselective beta-blockers (e.g., carteolol, carvedilol, levobunolol, metipranolol, nadolol, oxprenolol, penbutolol, pindolol, propranolol, sotalol, and timolol) are considered contraindicated in patients with bronchospastic diseases. Cardioselective beta-blockers should generally be avoided, or used with extreme caution if no other alternatives are available and the benefits outweigh the risks of potentially severe bronchospasm. If patients do receive this combination, they should be closely monitored for increased serum theophylline levels but decreased bronchodilatory effectiveness.', 'Antagonism', 'Oral and ophthalmic nonselective beta-blockers (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421189, 'Theophylline', 'St. John''s Wort', 'Moderate', 'Coadministration with St. John''s wort may decrease the serum concentrations of theophylline. The proposed mechanism is accelerated clearance of theophylline due to induction of hepatic CYP450 isoenzymes, namely 1A2, by constituents of St. John''s wort.', 'DDInter', 1767317476, 'Patients should consult a healthcare provider before taking any herbal or alternative medicine. Pharmacologic response and serum theophylline levels should be monitored more closely whenever St. John''s wort is added to or withdrawn from therapy, and the methylxanthine dosage adjusted as necessary. Patients should be advised to notify their physician if they experience loss of asthma control or worsening of respiratory symptoms.', 'Metabolism', 'Patients should consult a healthcare provider before taking any herbal or alternative medicine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421190, 'Theophylline', 'Stiripentol', 'Moderate', 'The concomitant administration of stiripentol may increase plasma concentrations and toxic effects of theophylline and caffeine. The mechanism is inhibition of CYP450 1A2 metabolism by stiripentol.', 'DDInter', 1767317476, 'The manufacturer recommends that concomitant use should be avoided.', 'Metabolism', 'The manufacturer recommends that concomitant use should be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421191, 'Theophylline', 'Succinylcholine', 'Moderate', 'Methylxanthines may antagonize the effects of neuromuscular blockers. The mechanism is unknown. In patients stabilized on aminophylline, resistance to pancuronium has been reported.', 'DDInter', 1767317476, 'The patient should be monitored for adequate neuromuscular blockade and the dosage adjusted, if necessary.', 'Antagonism', 'The patient should be monitored for adequate neuromuscular blockade and the dosage adjusted, if necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421192, 'Theophylline', 'Sucralfate', 'Minor', 'Limited clinical data suggest that sucralfate may inhibit the oral absorption of theophylline. The clinical significance of this interaction is unknown. Patients should be monitored for clinical and laboratory evidence of altered theophylline effect. Patients should be advised to take theophylline at least 2 hours before or 6 hours after sucralfate to minimize a potential interaction and to notify their physician if they experience a worsening of their respiratory symptoms.', 'DDInter', 1767317476, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421193, 'Theophylline', 'Sulfinpyrazone', 'Minor', 'In one study, concomitant administration of sulfinpyrazone resulted in a 22% increase in total theophylline clearance. Changes noted in the elimination of theophylline included an increase in metabolism and a decrease in renal excretion. The clinical significance of this interaction is unknown. If sulfinpyrazone and theophylline are given together, clinical monitoring of patient response and serum theophylline levels is recommended. Patients should be advised to report worsening of their respiratory symptoms to their physician.', 'DDInter', 1767317476, '-', 'Excretion', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
