INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421194, 'Theophylline', 'Tacrine', 'Moderate', 'Tacrine may increase plasma theophylline levels twofold and increase the elimination half-life of theophylline. The mechanism is probably related to the fact that both agents are metabolized by CYP450 1A2. Other hepatically metabolized methylxanthines may also be affected.', 'DDInter', 1767317476, 'If tacrine and a theophylline must be used concomitantly, dosage reductions are recommended, as is close observation for evidence of altered clinical and laboratory effects. Patients should be advised to report any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat to their physicians.', 'Metabolism', 'If tacrine and a theophylline must be used concomitantly, dosage reductions are recommended, as is close observation for evidence of altered clinical and laboratory effects.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421195, 'Theophylline', 'Tacrolimus', 'Moderate', 'Coadministration of sirolimus or tacrolimus with other drugs that are also metabolized by CYP450 3A4 may result in elevated plasma concentrations of the macrolide immunosuppressant and/or the coadministered drug(s). The mechanism is decreased drug clearance due to competitive inhibition of CYP450 3A4 activity.', 'DDInter', 1767317476, 'Pharmacologic responses and/or plasma drug levels should be monitored more closely whenever a macrolide immunosuppressant or another substrate of CYP450 3A4 is added to or withdrawn from therapy, and the dosage(s) adjusted as necessary.', 'Metabolism', 'Pharmacologic responses and/or plasma drug levels should be monitored more closely whenever a macrolide immunosuppressant or another substrate of CYP450 3A4 is added to or withdrawn from therapy, and the dosage(s) adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421196, 'Theophylline', 'Telithromycin', 'Moderate', 'INTERVAL: No clinically relevant pharmacokinetic interaction has been observed with telithromycin and theophylline administered as an extended-release formulation. However, simultaneous administration of theophylline and telithromycin may worsen gastrointestinal side effects such as nausea and vomiting, especially in female patients.', 'DDInter', 1767317476, 'Telithromycin and theophylline should be taken one hour apart to decrease the likelihood of gastrointestinal side effects.', 'Synergism', 'Telithromycin and theophylline should be taken one hour apart to decrease the likelihood of gastrointestinal side effects.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421197, 'Theophylline', 'Temazepam', 'Minor', 'Methylxanthines may antagonize the sedative effects of benzodiazepines. Aminophylline in particular has been demonstrated to attenuate the sedative effects of diazepam. The mechanism of this interaction may be related to competitive binding to intracerebral adenosine receptors.', 'DDInter', 1767317476, '-', 'Antagonism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421198, 'Theophylline', 'Temsirolimus', 'Moderate', 'Coadministration of sirolimus or tacrolimus with other drugs that are also metabolized by CYP450 3A4 may result in elevated plasma concentrations of the macrolide immunosuppressant and/or the coadministered drug(s). The mechanism is decreased drug clearance due to competitive inhibition of CYP450 3A4 activity.', 'DDInter', 1767317476, 'Pharmacologic responses and/or plasma drug levels should be monitored more closely whenever a macrolide immunosuppressant or another substrate of CYP450 3A4 is added to or withdrawn from therapy, and the dosage(s) adjusted as necessary.', 'Metabolism', 'Pharmacologic responses and/or plasma drug levels should be monitored more closely whenever a macrolide immunosuppressant or another substrate of CYP450 3A4 is added to or withdrawn from therapy, and the dosage(s) adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421199, 'Theophylline', 'Terbinafine', 'Moderate', 'The coadministration of terbinafine and theophylline may result in elevated plasma concentrations of the latter. The exact mechanism is unknown but may involve inhibition of the CYP450 1A2 enzymatic pathway, which is responsible for the metabolism of theophylline and other methylxanthines such as caffeine, the clearance of which has also been found to be reduced (21%) by terbinafine.', 'DDInter', 1767317476, 'Given the narrow therapeutic index of theophylline, patients should be monitored closely during concomitant therapy with terbinafine. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever terbinafine is added to or withdrawn from therapy. Due to the long elimination half-life of terbinafine, especially following prolonged use, potential drug interactions may be observed for several months after discontinuation of terbinafine therapy. Patients should be advised to report any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat to their physicians.', 'Metabolism', 'Given the narrow therapeutic index of theophylline, patients should be monitored closely during concomitant therapy with terbinafine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421200, 'Theophylline', 'Terbutaline', 'Moderate', 'Concomitant use of beta-2 adrenergic agonists with theophylline may increase the risk and/or severity of hypokalemia and adverse cardiovascular effects such as palpitation, tachycardia, and blood pressure elevation. Adverse events, especially hypokalemia, may be more likely with systemic or nebulized formulations of beta-2 agonists or high dosages of theophylline. Beta-2 agonists can cause clinically significant but usually transient decreases in serum potassium concentrations, while hypokalemia associated with theophylline often occurs in toxicity. Pharmacokinetically, some beta-2 agonists given systemically may decrease the plasma concentrations of theophylline.', 'DDInter', 1767317476, 'Although theophylline and beta-2 agonists are commonly used together to produce bronchodilation, it may be appropriate to monitor patient response as well as serum potassium level, blood pressure and heart rate during coadministration, especially if the beta-2 agonist is administered systemically or by nebulizer. Close monitoring is particularly important in patients with severe asthma, since the potential increases in blood pressure and heart rate may have more serious consequences in the presence of hypoxemia or hypercapnia due to increased myocardial oxygen consumption. Patients should be advised to notify their physician if they experience worsening of their respiratory condition or potential signs and symptoms of hypokalemia such as fatigue, weakness, myalgia, muscle cramps, numbness, tingling, abdominal pain, constipation, palpitation, and irregular heartbeat.', 'Synergism', 'Although theophylline and beta-2 agonists are commonly used together to produce bronchodilation, it may be appropriate to monitor patient response as well as serum potassium level, blood pressure and heart rate during coadministration, especially if the beta-2 agonist is administered systemically or by nebulizer.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421201, 'Theophylline', 'Teriflunomide', 'Moderate', 'Coadministration with teriflunomide may decrease the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 1A2. The proposed mechanism is increased clearance due to teriflunomide-mediated induction of CYP450 1A2.', 'DDInter', 1767317476, 'Caution is advised if teriflunomide is used concomitantly with drugs that are substrates of CYP450 1A2. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever teriflunomide is added to or withdrawn from therapy with these drugs.', 'Metabolism', 'Caution is advised if teriflunomide is used concomitantly with drugs that are substrates of CYP450 1A2.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421202, 'Theophylline', 'Thallous Chloride', 'Moderate', 'Coadministration of certain drugs may alter the myocardial uptake of thallous chloride Tl-201 and interfere with results of myocardial imaging studies. Drugs that increase or decrease coronary blood flow or potassium uptake may alter the biodistribution of thallous chloride Tl-201.', 'DDInter', 1767317476, 'Clinicians should be aware of possible diagnostic interference by these drugs.', 'Others', 'Clinicians should be aware of possible diagnostic interference by these drugs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421203, 'Theophylline', 'Thiabendazole', 'Moderate', 'Theophylline toxicity has been reported during concomitant administration of theophylline and thiabendazole. Decreases in clearance have been noted up to nine days after discontinuation of thiabendazole. The proposed mechanism is inhibition of hepatic metabolism by thiabendazole.', 'DDInter', 1767317476, 'Clinical monitoring of patient response and tolerance and serum theophylline levels is recommended. Patients should be advised to report any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat to their physicians. The theophylline dosage may require reduction or alternatives agents could be considered during thiabenzole therapy.', 'Metabolism', 'Clinical monitoring of patient response and tolerance and serum theophylline levels is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421204, 'Thiopental', 'Theophylline', 'Moderate', 'Barbiturates may decrease serum levels and therapeutic effects of the methylxanthines. The mechanism is barbiturate induction of CYP450 3A4 and 1A2 hepatic metabolism of methylxanthines.', 'DDInter', 1767317476, 'Close observation for clinical and laboratory evidence of decreased methylxanthine effect is indicated if these drugs must be used together. Patients should be advised to notify their physician if they experience a worsening of their respiratory symptoms.', 'Metabolism', 'Close observation for clinical and laboratory evidence of decreased methylxanthine effect is indicated if these drugs must be used together.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421205, 'Thyroid, porcine', 'Theophylline', 'Moderate', 'The metabolism of theophylline is directly related to thyroid function. Theophylline clearance is decreased in hypothyroid patients and increased in hyperthyroid patients. Toxicity or subtherapeutic levels may occur. Similar interactions may occur with other methylxanthines.', 'DDInter', 1767317476, 'When thyroid hormone dosage is instituted, discontinued, or changed, close observation for altered theophylline effect and serum concentrations is recommended. Patients should be advised to notify their physician if they experience a worsening of their respiratory symptoms, or any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeats.', 'Others', 'When thyroid hormone dosage is instituted, discontinued, or changed, close observation for altered theophylline effect and serum concentrations is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421206, 'Ticlopidine', 'Theophylline', 'Moderate', 'In one study, theophylline clearance was reduced by 37% and theophylline half-life increased by 42% during concomitant ticlopidine therapy. The exact mechanism of this interaction is not known, but ticlopidine probably interferes with theophylline metabolism.', 'DDInter', 1767317476, 'Theophylline levels and clinical response should be monitored when ticlopidine therapy is initiated or discontinued. Patients should be advised to report any signs of theophylline toxicity during concurrent therapy, including nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat to their physician.', 'Metabolism', 'Theophylline levels and clinical response should be monitored when ticlopidine therapy is initiated or discontinued.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421207, 'Timolol', 'Theophylline', 'Major', 'The pharmacologic effects of theophyllines and beta-blockers are opposite. Nonselective and high doses of cardioselective beta-blockers may cause severe or fatal bronchospasm by opposing theophylline-induced bronchodilation. Ophthalmic beta-blockers undergo significant systemic absorption and may also interact. In addition, propranolol and other beta-blockers may reduce the CYP450 hepatic metabolism of theophylline, and serum theophylline levels may be increased.', 'DDInter', 1767317476, 'Oral and ophthalmic nonselective beta-blockers (e.g., carteolol, carvedilol, levobunolol, metipranolol, nadolol, oxprenolol, penbutolol, pindolol, propranolol, sotalol, and timolol) are considered contraindicated in patients with bronchospastic diseases. Cardioselective beta-blockers should generally be avoided, or used with extreme caution if no other alternatives are available and the benefits outweigh the risks of potentially severe bronchospasm. If patients do receive this combination, they should be closely monitored for increased serum theophylline levels but decreased bronchodilatory effectiveness.', 'Antagonism', 'Oral and ophthalmic nonselective beta-blockers (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421208, 'Tocainide', 'Theophylline', 'Minor', 'Tocainide causes a decrease in theophylline clearance. The result is an increase in serum theophylline concentrations and a mild increase in the serum half-life of theophylline. The mechanism of this interaction is not known. Tocainide may interact in a similar way with other xanthines. Management consists of monitoring clinical status during therapy and checking serum theophylline levels as necessary. Patients should be advised to report any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, or irregular heartbeat to their physician.', 'DDInter', 1767317476, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421209, 'Tocilizumab', 'Theophylline', 'Moderate', 'Plasma concentrations of drugs that are CYP450 substrates may decrease following the initiation of interleukin (IL) inhibitors, tumor necrosis factor (TNF) blockers, or interferon (IFN) inhibitors in patients with chronic inflammatory diseases. Because the formation of hepatic CYP450 enzymes is down-regulated during infection and chronic inflammation by increased levels of certain cytokines (e.g., interleukins-1, -6, and -10; tumor necrosis factor alpha; interferons), treatment targeting these cytokines may restore or normalize CYP450 enzyme levels resulting in increased metabolism of these drugs.', 'DDInter', 1767317476, 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.g., antiarrhythmics, anticonvulsants, immunosuppressants, theophylline) or sensitive substrates where decreases in plasma levels may be significant or undesirable (e.g., oral contraceptives, statins, benzodiazepines, opioids). Clinical and/or laboratory monitoring should be considered following the initiation or withdrawal of such treatments, and the dosage(s) of the CYP450 substrate(s) adjusted accordingly. The effects of IL inhibitors, TNF blockers, and IFN inhibitors on CYP450 activities may persist for several weeks after stopping therapy.', 'Metabolism', 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421210, 'Triazolam', 'Theophylline', 'Minor', 'Methylxanthines may antagonize the sedative effects of benzodiazepines. Aminophylline in particular has been demonstrated to attenuate the sedative effects of diazepam. The mechanism of this interaction may be related to competitive binding to intracerebral adenosine receptors.', 'DDInter', 1767317476, '-', 'Antagonism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421211, 'Troleandomycin', 'Theophylline', 'Moderate', 'Coadministration with certain macrolide antibiotics may increase the serum concentrations of theophylline, which may result in toxicity. The proposed mechanism is macrolide inhibition of CYP450 3A4, the isoenzyme partially responsible for the metabolic clearance of theophylline.', 'DDInter', 1767317476, 'Pharmacologic response and serum theophylline levels should be monitored more closely whenever a macrolide antibiotic is added to or withdrawn from therapy, and the methylxanthine dosage adjusted as necessary.', 'Metabolism', 'Pharmacologic response and serum theophylline levels should be monitored more closely whenever a macrolide antibiotic is added to or withdrawn from therapy, and the methylxanthine dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421212, 'Tubocurarine', 'Theophylline', 'Moderate', 'Methylxanthines may antagonize the effects of neuromuscular blockers. The mechanism is unknown. In patients stabilized on aminophylline, resistance to pancuronium has been reported.', 'DDInter', 1767317476, 'The patient should be monitored for adequate neuromuscular blockade and the dosage adjusted, if necessary.', 'Antagonism', 'The patient should be monitored for adequate neuromuscular blockade and the dosage adjusted, if necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421213, 'Ustekinumab', 'Theophylline', 'Moderate', 'Plasma concentrations of drugs that are CYP450 substrates may decrease following the initiation of interleukin (IL) inhibitors, tumor necrosis factor (TNF) blockers, or interferon (IFN) inhibitors in patients with chronic inflammatory diseases. Because the formation of hepatic CYP450 enzymes is down-regulated during infection and chronic inflammation by increased levels of certain cytokines (e.g., interleukins-1, -6, and -10; tumor necrosis factor alpha; interferons), treatment targeting these cytokines may restore or normalize CYP450 enzyme levels resulting in increased metabolism of these drugs.', 'DDInter', 1767317476, 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.g., antiarrhythmics, anticonvulsants, immunosuppressants, theophylline) or sensitive substrates where decreases in plasma levels may be significant or undesirable (e.g., oral contraceptives, statins, benzodiazepines, opioids). Clinical and/or laboratory monitoring should be considered following the initiation or withdrawal of such treatments, and the dosage(s) of the CYP450 substrate(s) adjusted accordingly. The effects of IL inhibitors, TNF blockers, and IFN inhibitors on CYP450 activities may persist for several weeks after stopping therapy.', 'Metabolism', 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421214, 'Vecuronium', 'Theophylline', 'Moderate', 'Methylxanthines may antagonize the effects of neuromuscular blockers. The mechanism is unknown. In patients stabilized on aminophylline, resistance to pancuronium has been reported.', 'DDInter', 1767317476, 'The patient should be monitored for adequate neuromuscular blockade and the dosage adjusted, if necessary.', 'Antagonism', 'The patient should be monitored for adequate neuromuscular blockade and the dosage adjusted, if necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421215, 'Vemurafenib', 'Theophylline', 'Moderate', 'Coadministration with vemurafenib may increase the plasma concentrations of drugs that are substrates of the CYP450 1A2 and/or 2D6 isoenzymes. The mechanism is decreased clearance due to inhibition of CYP450 1A2 and 2D6 activity by vemurafenib. The interaction has been studied with caffeine and dextromethorphan, probe substrates for CYP450 1A2 and 2D6, respectively.', 'DDInter', 1767317476, 'Caution is advised if vemurafenib must be used concurrently with drugs that undergo metabolism by CYP450 1A2 and/or 2D6. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever vemurafenib is added to or withdrawn from therapy. Concomitant use of vemurafenib and substrates of CYP450 1A2 and/or 2D6 with a narrow therapeutic index (e.g., antiarrhythmics; theophylline) should be avoided if possible.', 'Metabolism', 'Caution is advised if vemurafenib must be used concurrently with drugs that undergo metabolism by CYP450 1A2 and/or 2D6.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421216, 'Voriconazole', 'Theophylline', 'Moderate', 'Coadministration with voriconazole may increase the plasma concentrations of drugs that are substrates of CYP450 2C19, 2C9, and/or 3A4. The mechanism is decreased clearance due to inhibition of those isoenzymes by voriconazole. Increased plasma levels and/or pharmacologic effects of drugs such as cyclosporine, sirolimus, tacrolimus (3A4 substrates), and warfarin (2C9 substrate) have been reported during coadministration with voriconazole.', 'DDInter', 1767317476, 'Caution is advised if voriconazole must be used concomitantly with medications that undergo metabolism by CYP450 2C19, 2C9 and/or 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever voriconazole is added to or withdrawn from therapy. The manufacturer specifically recommends that a dosage reduction be considered for benzodiazepines, statins, long-acting opiates, and vinca alkaloids that are metabolized by the affected isoenzymes when used with voriconazole.', 'Metabolism', 'Caution is advised if voriconazole must be used concomitantly with medications that undergo metabolism by CYP450 2C19, 2C9 and/or 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421217, 'Zafirlukast', 'Theophylline', 'Moderate', 'The coadministration of theophylline with zafirlukast may modestly reduce zafirlukast plasma concentrations. The mechanism is unknown.', 'DDInter', 1767317476, 'If there is any change in either drug''s regimen, the patient should be observed for altered theophylline and zafirlukast effect, and theophylline levels should be monitored closely. Other methylxanthines may interact with zafirlukast in a similar manner.', 'Others', 'If there is any change in either drug''s regimen, the patient should be observed for altered theophylline and zafirlukast effect, and theophylline levels should be monitored closely.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421218, 'Zileuton', 'Theophylline', 'Moderate', 'Coadministration with zileuton may significantly increase the plasma concentrations of theophylline. The proposed mechanism is zileuton inhibition of theophylline metabolism via CYP450 1A2.', 'DDInter', 1767317476, 'Caution is advised if theophylline is prescribed in combination with zileuton, particularly in the elderly and patients with chronic obstructive pulmonary disease, congestive heart failure or cirrhosis, as these patients generally have lower theophylline clearance rates and may be particularly vulnerable to theophylline toxicity. When zileuton is initiated in patients receiving theophylline, the theophylline dosage should be reduced by approximately one-half and theophylline levels closely monitored. Similarly, when initiating therapy with theophylline in patients receiving zileuton, the maintenance dosage and/or dosing interval of theophylline should be adjusted accordingly and guided by serum theophylline levels. Patients should be advised to notify their physician if they experience signs and symptoms suggestive of theophylline toxicity such as nausea, vomiting, diarrhea, headache, tremor, irritability, insomnia, seizures, palpitation, or arrhythmia.', 'Metabolism', 'Caution is advised if theophylline is prescribed in combination with zileuton, particularly in the elderly and patients with chronic obstructive pulmonary disease, congestive heart failure or cirrhosis, as these patients generally have lower theophylline clearance rates and may be particularly vulnerable to theophylline toxicity.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421219, 'Eltrombopag', 'Aluminum hydroxide', 'Moderate', 'INTERVAL: Products containing aluminum, calcium, iron, magnesium, zinc and other polyvalent cations such as antacids or mineral supplements can significantly reduce the gastrointestinal absorption of eltrombopag due to chelation.', 'DDInter', 1767317476, 'Eltrombopag should be taken on an empty stomach at least 2 hours before or 4 hours after oral medications that contain antacids (e.g., didanosine buffered tablets or pediatric oral solution), aluminum, calcium, iron, magnesium, zinc or other polyvalent cations.', 'Absorption', 'Eltrombopag should be taken on an empty stomach at least 2 hours before or 4 hours after oral medications that contain antacids (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421220, 'Eltrombopag', 'Artesunate', 'Moderate', 'Coadministration with eltrombopag may increase the plasma concentrations of drugs that are substrates of the organic anion transporting polypeptide (OATP) 1B1 and/or breast cancer resistance protein (BCRP) transporters. The mechanism is decreased clearance due to inhibition of OATP1B1-mediated hepatic uptake and BCRP-mediated intestinal and hepatobiliary efflux by eltrombopag. The interaction has been demonstrated for rosuvastatin, a known substrate of both OATP 1B1 and BCRP.', 'DDInter', 1767317476, 'Caution is advised during concomitant use of eltrombopag with drugs that are substrates of the OATP1B1 and/or BCRP transporters, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever eltrombopag is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised during concomitant use of eltrombopag with drugs that are substrates of the OATP1B1 and/or BCRP transporters, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
