INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421235, 'Eltrombopag', 'Echinacea', 'Moderate', 'Coadministration with echinacea may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of CYP450 1A2. The proposed mechanism is decreased clearance due to echinacea-mediated inhibition of CYP450 1A2.', 'DDInter', 1767317476, 'Caution is advised if echinacea is used concomitantly with drugs that are substrates of CYP450 1A2, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever echinacea is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Caution is advised if echinacea is used concomitantly with drugs that are substrates of CYP450 1A2, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421236, 'Eluxadoline', 'Eltrombopag', 'Major', 'Coadministration with inhibitors of organic anion transporting polypeptide (OATP) 1B1 may significantly increase the plasma concentrations of eluxadoline. Based on available data, inhibition of OATP1B1-mediated hepatic uptake appears to reduce both the hepatic first-pass extraction and systemic biliary clearance of eluxadoline.', 'DDInter', 1767317476, 'The recommended dosage of eluxadoline is 75 mg twice daily when used with OATP1B1 inhibitors. Patients should be monitored for adverse effects such as sedation, nausea, vomiting, constipation, abdominal pain, liver enzyme elevations, and pancreatitis.', 'Metabolism', 'The recommended dosage of eluxadoline is 75 mg twice daily when used with OATP1B1 inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421237, 'Enasidenib', 'Eltrombopag', 'Moderate', 'Based on in vitro data, coadministration with enasidenib may increase the plasma concentrations of drugs that are substrates of the transporters, organic anion transporting polypeptides (OATP) 1B1/1B3 and breast cancer resistance protein (BCRP). The proposed mechanism is decreased clearance due to enasidenib -mediated inhibition of OATP1B1/1B3 and BCRP transporters.', 'DDInter', 1767317476, 'Caution is advised when enasidenib is used concurrently with medications that are substrates of OATP 1B1/1B3 and/or BCRP, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever enasidenib is added to or withdrawn from therapy.', 'Distribution', 'Caution is advised when enasidenib is used concurrently with medications that are substrates of OATP 1B1/1B3 and/or BCRP, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421238, 'Encorafenib', 'Eltrombopag', 'Moderate', 'Coadministration with encorafenib may theoretically increase the plasma concentrations of drugs that are substrates of P-glycoprotein (P-gp), breast cancer resistance protein (BCRP), organic cation transporter (OCT2), organic anion transporter (OAT1, OAT3), or organic anion transporting polypeptide (OATP1B1, OATP1B3). The proposed mechanism is decreased clearance due to inhibition of the corresponding transporter by encorafenib.', 'DDInter', 1767317476, 'Caution is advised if encorafenib must be used concomitantly with drugs that are substrates of the affected transporters, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever encorafenib is added to or withdrawn from therapy.', 'Excretion', 'Caution is advised if encorafenib must be used concomitantly with drugs that are substrates of the affected transporters, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421239, 'Entrectinib', 'Eltrombopag', 'Moderate', 'Coadministration with entrectinib may increase the plasma concentrations of drugs that are substrates of the breast cancer resistance protein (BCRP) transporter. The proposed mechanisms, based on in vitro data, is decreased clearance due to entrectinib-mediated inhibition of BCRP transport protein.', 'DDInter', 1767317476, 'Caution is advised when entrectinib is prescribed with drugs that are BCRP substrates, particularly those with a narrow therapeutic range such as methotrexate, mitoxantrone, topotecan, and lapatinib. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever entrectinib is added to or withdrawn from therapy.', 'Others', 'Caution is advised when entrectinib is prescribed with drugs that are BCRP substrates, particularly those with a narrow therapeutic range such as methotrexate, mitoxantrone, topotecan, and lapatinib.', NULL, NULL, 'Moderate', 'pharmacodynamic');
