INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421300, 'Eltrombopag', 'Rimegepant', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may significantly increase the plasma concentrations of rimegepant. The proposed mechanism is decreased clearance due to inhibition of P-gp- and/or BCRP-mediated metabolism of rimegepant.', 'DDInter', 1767317476, 'Coadministration of rimegepant with P-gp and/or BCRP inhibitors should be avoided.', 'Metabolism', 'Coadministration of rimegepant with P-gp and/or BCRP inhibitors should be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
