INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421321, 'Amoxapine', 'Eluxadoline', 'Moderate', 'The risk of constipation and serious constipation-related adverse reactions may be increased when eluxadoline is used with other drugs that are also associated with this adverse effect.', 'DDInter', 1767317476, 'Concomitant use of eluxadoline with other drugs that can cause constipation such as alosetron, anticholinergics, and opioids should generally be avoided. Loperamide may be used occasionally for acute management of severe diarrhea, but chronic use is not recommended, and it should be discontinued immediately if constipation occurs. Eluxadoline should also be discontinued if constipation occurs for more than 4 days.', 'Synergism', 'Concomitant use of eluxadoline with other drugs that can cause constipation such as alosetron, anticholinergics, and opioids should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421322, 'Amprenavir', 'Eluxadoline', 'Moderate', 'It is not known whether, and to what extent, eluxadoline may be metabolized by CYP450 isoenzymes and may interact with inhibitors of CYP450 isoenzymes.', 'DDInter', 1767317476, 'As a precautionary measure due to incomplete information on the metabolism of eluxadoline, the manufacturer recommends caution during coadministration with potent inhibitors of various CYP450 isoenzymes. Patients should be monitored for adverse effects such as sedation, nausea, vomiting, constipation, abdominal pain, liver enzyme elevations, and pancreatitis,', 'Metabolism', 'As a precautionary measure due to incomplete information on the metabolism of eluxadoline, the manufacturer recommends caution during coadministration with potent inhibitors of various CYP450 isoenzymes.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421323, 'Eluxadoline', 'Artesunate', 'Major', 'Coadministration with inhibitors of organic anion transporting polypeptide (OATP) 1B1 may significantly increase the plasma concentrations of eluxadoline. Based on available data, inhibition of OATP1B1-mediated hepatic uptake appears to reduce both the hepatic first-pass extraction and systemic biliary clearance of eluxadoline.', 'DDInter', 1767317476, 'The recommended dosage of eluxadoline is 75 mg twice daily when used with OATP1B1 inhibitors. Patients should be monitored for adverse effects such as sedation, nausea, vomiting, constipation, abdominal pain, liver enzyme elevations, and pancreatitis.', 'Metabolism', 'The recommended dosage of eluxadoline is 75 mg twice daily when used with OATP1B1 inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421324, 'Eluxadoline', 'Astemizole', 'Moderate', 'It is not known whether, and to what extent, eluxadoline may inhibit CYP450 3A4 and may interact with substrates of the isoenzyme. In vitro studies have shown that eluxadoline is not an inhibitor of CYP450 3A4 at clinically relevant systemic concentrations, but they were not adequate to rule out inhibition of CYP450 3A4 in the gut by eluxadoline. Therefore, the potential for eluxadoline to increase plasma concentrations of oral drugs that are metabolized by CYP450 3A4 should be considered.', 'DDInter', 1767317476, 'Caution is advised when eluxadoline is used with sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges. These drugs include, but are not limited to: cisapride, colchicine, cyclosporine, fentanyl, ivacaftor, lovastatin, simvastatin, oral midazolam, triazolam, pimozide, quinidine, sildenafil, ergot derivatives, and macrolide immunosuppressants.', 'Others', 'Caution is advised when eluxadoline is used with sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges.', NULL, NULL, 'Moderate', 'pharmacodynamic');
