INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421453, 'Elvitegravir', 'Aluminum hydroxide', 'Moderate', 'INTERVAL: The concomitant use of antacids may significantly decrease the plasma concentrations of elvitegravir. The mechanism of interaction has not been described. In contrast, when the antacid was given 4 hours before or 4 hours after elvitegravir, no significant differences were observed in elvitegravir pharmacokinetics compared to administration of elvitegravir alone.', 'DDInter', 1767317476, 'Antacid-containing preparations should be administered at least 2 hours before or 2 hours after the elvitegravir dose. Alternatively, H2-receptor antagonists or proton pump inhibitors may be considered, with no dosage adjustment needed.', 'Others', 'Antacid-containing preparations should be administered at least 2 hours before or 2 hours after the elvitegravir dose.', NULL, NULL, 'Moderate', 'pharmacodynamic');
