INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421455, 'Enzalutamide', 'Elvitegravir', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of elvitegravir, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic drug levels, concomitant use of elvitegravir with potent CYP450 3A4 inducers is generally not recommended. In addition, other antiretroviral agents that are typically combined with elvitegravir may be contraindicated for use with potent CYP450 3A4 inducers.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic drug levels, concomitant use of elvitegravir with potent CYP450 3A4 inducers is generally not recommended.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421456, 'Erdafitinib', 'Elvitegravir', 'Major', 'Coadministration with moderate inducers of CYP450 2C9 or 3A4 may decrease the plasma concentrations and therapeutic effects of erdafitinib. The mechanism is induction of CYP450 2C9- or 3A4-mediated metabolism of erdafitinib, which has been shown to be metabolized by these isoenzymes.', 'DDInter', 1767317476, 'According to the manufacturer, if a moderate CYP450 2C9 or 3A4 inducer is coadministered at the start of erdafitinib therapy, the recommended dose of erdafitinib should be administered. If a moderate CYP450 2C9 or 3A4 inducer is coadministered after the initial dose increase period, the dose of erdafitinib may be increased up to 9 mg once daily based on serum phosphate levels and tolerability. When a moderate CYP450 2C9 or 3A4 inducer is discontinued, the current dose of erdafitinib may be continued in the absence of erdafitinib toxicity.', 'Metabolism', 'According to the manufacturer, if a moderate CYP450 2C9 or 3A4 inducer is coadministered at the start of erdafitinib therapy, the recommended dose of erdafitinib should be administered.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421457, 'Eslicarbazepine', 'Elvitegravir', 'Moderate', 'Coadministration with inducers of CYP450 3A4 isoenzymes may reduce the plasma concentrations of elvitegravir, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, the manufacturers of elvitegravir single-ingredient and combination products recommend avoiding concomitant use. Alternative treatment combinations that do not include metabolic inducers should be considered whenever possible.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, the manufacturers of elvitegravir single-ingredient and combination products recommend avoiding concomitant use.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421458, 'Etonogestrel', 'Elvitegravir', 'Moderate', 'RECOMMENDED: Coadministration with cobicistat and elvitegravir may alter the plasma concentrations of contraceptive hormones. The exact mechanism of interaction has not been described. Clinical effects of increased progestogen levels are not fully known, but may include increased risk of insulin resistance, dyslipidemia, acne, and venous thrombosis.', 'DDInter', 1767317476, 'The potential risks and benefits of using norgestimate-containing contraceptives in combination with cobicistat and elvitegravir should be considered, particularly in women who have risk factors for insulin resistance, dyslipidemia, acne, and venous thrombosis. Coadministration of cobicistat and elvitegravir with other hormonal contraceptives (e.g., contraceptive patch, contraceptive vaginal ring, or injectable contraceptives), oral contraceptives containing progestogens other than norgestimate, or less than 25 mcg of ethinyl estradiol has not been studied and is not recommended. Some authorities recommend that hormonal contraceptives containing at least 30 mcg of ethinyl estradiol with norgestimate should be used in combination with the multi-ingredient antiretroviral formulations containing elvitegravir/cobicistat/emtricitabine/tenofovir disoproxil fumarate or elvitegravir/cobicistat/emtricitabine/tenofovir alafenamide. However, the long term effects of increased progesterone exposure are not known. Alternative, nonhormonal methods of contraception may be considered.', 'Others', 'The potential risks and benefits of using norgestimate-containing contraceptives in combination with cobicistat and elvitegravir should be considered, particularly in women who have risk factors for insulin resistance, dyslipidemia, acne, and venous thrombosis.', NULL, NULL, 'Moderate', 'pharmacodynamic');
