INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421488, 'Elvitegravir', 'Zinc acetate', 'Major', 'INTERVAL: Coadministration with supplements containing polyvalent cations such as zinc may decrease the plasma concentrations of integrase strand transfer inhibitors (INSTIs) such as elvitegravir and raltegravir. The mechanism involves chelation of the INSTI by polyvalent cations, forming a complex that is poorly absorbed from the gastrointestinal tract. The clinical relevance is unknown.', 'DDInter', 1767317476, 'If concomitant use is required, monitor for virologic efficacy and administer the INSTI such as elvitegravir or raltegravir at least 2 hours before or 6 hours after supplements containing polyvalent cations such as zinc.', 'Absorption', 'If concomitant use is required, monitor for virologic efficacy and administer the INSTI such as elvitegravir or raltegravir at least 2 hours before or 6 hours after supplements containing polyvalent cations such as zinc.', NULL, NULL, 'Major', 'pharmacodynamic');
