INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (162517, 'St. John''s Wort', 'Romidepsin', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 may increase the plasma concentrations of romidepsin. The exact mechanism of interaction has not been established, particularly since romidepsin is a substrate of CYP450 3A4, and induction of the isoenzyme would be expected to reduce its plasma concentration.', NULL, 'The use of romidepsin in combination with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St.', NULL, 'The use of romidepsin in combination with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St. John''s wort should generally be avoided if possible. Alternative treatment lacking CYP450 3A4-inducing activity should be considered in patients receiving romidepsin. If concomitant use is required, patients should be closely monitored for development of hematologic toxicities such as anemia, leukopenia, and thrombocytopenia as well as electrocardiographic changes such as QT interval prolongation and T-wave and ST-segment changes.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/162690/', '[1] "Product Information. Istodax (romidepsin)." Gloucester Pharmaceuticals, Cambridge, MA.[2] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767363025);
