INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (162598, 'Fostamatinib', 'Ropivacaine', 'Moderate', 'Coadministration with fostamatinib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme and/or P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine and/or liver due to inhibition of CYP450 3A4-mediated metabolism and P-gp-mediated efflux by fostamatinib.', NULL, 'Caution is advised when fostamatinib is used concurrently with drugs that are known substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when fostamatinib is used concurrently with drugs that are known substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fostamatinib is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/162771/', '[1] "Product Information. Tavalisse (fostamatinib)." Rigel Pharmaceuticals, South San Francisco, CA.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (162599, 'Givosiran', 'Ropivacaine', 'Moderate', 'Coadministration with givosiran may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of CYP450 1A2 and/or CYP450 2D6. The proposed mechanism is decreased clearance due to givosiran-mediated inhibition of CYP450 1A2 and 2D6 isoenzymes.', NULL, 'Caution and monitoring are recommended when givosiran is given with drugs that are substrates of CYP450 1A2 and/or 2D6.', NULL, 'Caution and monitoring are recommended when givosiran is given with drugs that are substrates of CYP450 1A2 and/or 2D6. Concomitant use should generally be avoided with CYP450 1A2 or 2D6 substrates that have a narrow therapeutic range where minimal concentration changes may lead to serious or life-threatening toxicities. If concomitant use is unavoidable, the dosage of the CYP450 1A2 or 2D6 substrate should be reduced according to approved product labeling or clinical response and tolerance. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever givosiran is added to or withdrawn from therapy with these drugs.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/162772/', '[1] "Product Information. Givlaari (givosiran)." Alnylam Pharmaceuticals, Cambridge, MA.', 1767363025);
