INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421711, 'Empagliflozin', 'Phenobarbital', 'Moderate', 'Coadministration of empagliflozin with inducers of uridine diphosphate glucuronosyl transferase (UGT) enzymes may decrease the plasma concentrations and efficacy of empagliflozin, which is primarily metabolized in vitro by UGT1A3, 1A8, 1A9, and 2B7.', 'DDInter', 1767317476, 'Coadministration of empagliflozin with UGT inducers should be avoided if possible. If concomitant use is required, dosage adjustments as well as increased clinical and laboratory monitoring should be considered whenever a UGT inducer is added to or withdrawn from empagliflozin therapy.', 'Metabolism', 'Coadministration of empagliflozin with UGT inducers should be avoided if possible.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421712, 'Empagliflozin', 'Phenoxybenzamine', 'Moderate', 'Sodium-glucose co-transporter 2 (SGLT-2) inhibitors may potentiate the hypotensive effects of diuretics and other antihypertensive agents or vasodilators. Inhibition of glucose and sodium co-transport produces mild diuresis and transient natriuresis, resulting in intravascular volume contraction. Volume depletion-related adverse reactions including hypotension, postural dizziness, orthostatic hypotension, syncope, and dehydration can occur after initiating treatment with SGLT-2 inhibitors, and the risk may be increased with concomitant use of other agents that can lower blood pressure.', 'DDInter', 1767317476, 'Caution is advised if SGLT-2 inhibitors are coadministered with diuretics and other hypotensive agents, particularly in the elderly and patients with impaired renal function. Prior to initiating SGLT-2 inhibitors, volume status should be assessed and corrected, if necessary. Clinical and laboratory monitoring are recommended during therapy, including electrolytes, fluid status, renal function, and blood pressure. If volume depletion occurs, treatment with SGLT-2 inhibitors should be interrupted until the condition is corrected.', 'Synergism', 'Caution is advised if SGLT-2 inhibitors are coadministered with diuretics and other hypotensive agents, particularly in the elderly and patients with impaired renal function.', NULL, NULL, 'Moderate', 'pharmacodynamic');
