INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421779, 'Empagliflozin', 'Tolazamide', 'Moderate', 'Coadministration of a sodium-glucose co-transporter 2 (SGLT-2) inhibitor with an insulin secretagogue (e.g., sulfonylurea, meglitinide) or insulin may potentiate the risk of hypoglycemia.', 'DDInter', 1767317476, 'A lower dosage of the insulin secretagogue or insulin may be required when used in combination with an SGLT-2 inhibitor.', 'Synergism', 'A lower dosage of the insulin secretagogue or insulin may be required when used in combination with an SGLT-2 inhibitor.', NULL, NULL, 'Moderate', 'pharmacodynamic');
