INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (163100, 'Telaprevir', 'Rucaparib', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations and the risk of adverse effects of rucaparib, which has been shown in vitro to be a substrate of the isoenzyme. However, the effects on the pharmacokinetics of rucaparib have not been evaluated in vivo.', NULL, 'Caution is advised if rucaparib is used concomitantly with potent CYP450 3A4 inhibitors.', NULL, 'Caution is advised if rucaparib is used concomitantly with potent CYP450 3A4 inhibitors. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever a potent CYP450 3A4 inhibitor is added to or withdrawn from therapy with rucaparib.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/163273/', '[1] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):', 1767363025);
