INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (163160, 'Zanubrutinib', 'Rucaparib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of zanubrutinib, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised when zanubrutinib is used with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when zanubrutinib is used with CYP450 3A4 inhibitors. Patients should be monitored for increased adverse effects such as rash, diarrhea, constipation, cough, hemorrhage, infection, cytopenias, and atrial fibrillation or flutter, and the zanubrutinib dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/163333/', '[1] "Product Information. Brukinsa (zanubrutinib)." BeiGene USA, Inc, San Mateo, CA.[2] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.[3] "Product Information. Brukinsa (zanubrutinib)." BeiGene USA, Inc, San Mateo, CA.', 1767363025);
