INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (163301, 'Tiagabine', 'Rufinamide', 'Moderate', 'Rufinamide may modestly or moderately decrease the plasma concentrations and therapeutic effects of drugs that are CYP450 3A4 substrates. The mechanism is enhanced metabolism by CYP450 3A4 enzyme induction.', NULL, 'During concomitant treatment with rufinamide and drugs that are CYP450 3A4 substrates, it is recommended that patients should be monitored for 2 weeks when rufinamide is added to or withdrawn from therapy, or after increases of the dose.', NULL, 'During concomitant treatment with rufinamide and drugs that are CYP450 3A4 substrates, it is recommended that patients should be monitored for 2 weeks when rufinamide is added to or withdrawn from therapy, or after increases of the dose. Dose adjustments of the coadministered drug may be required if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/163474/', '[1] "Product Information. Banzel (rufinamide)." Eisai Inc, Teaneck, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Yamreudeewong W, Henann NE, Fazio A, Lower DL, Cassidy TG "Drug-food interactions in clinical practice." J Fam Pract 40 (1995): 376-84[4] "Product Information. Ambien (zolpidem)." sanofi-aventis, Bridgewater, NJ.[5] "Product Information. Banzel (rufinamide)." Eisai Inc, Teaneck, NJ.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (163302, 'Ticagrelor', 'Rufinamide', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ticagrelor, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of ticagrelor with CYP450 3A4 inducers should generally be avoided.', NULL, 'Concomitant use of ticagrelor with CYP450 3A4 inducers should generally be avoided. Otherwise, caution is advised, and patients should be closely monitored for diminished clinical response to ticagrelor therapy. Alternative treatment may be required if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/163475/', '[1] "Product Information. Brilinta (ticagrelor)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.[2] Weeks P, Sieg A, Vahdat K, Raissi F, Nathan S "Improved ticagrelor antiplatelet effect on discontinuation of phenytoin." Ann Pharmacother 48 (2014): 644-7[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Teng R, Mitchell P, Butler K "Effect of rifampicin on the pharmacokinetics and pharmacodynamics of ticagrelor in healthy subjects." Eur J Clin Pharmacol 69 (2013): 877-83[5] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (163303, 'Tizanidine', 'Rufinamide', 'Moderate', 'The sedative effect of tizanidine may be potentiated by concomitant use of other agents with central nervous system (CNS) depressant effects. In addition, tizanidine and many of these agents (e.g., alcohol, anxiolytics, sedatives, hypnotics, antidepressants, antipsychotics, opioids, muscle relaxants) also can exhibit hypotensive effects, which may be additive during coadministration and may increase the risk of symptomatic hypotension and orthostasis, particularly during initiation of therapy or dose escalation. Tizanidine itself is a central alpha-2 adrenergic agonist. Pharmacologic studies have found tizanidine to possess between 1/10 to 1/50 of the potency of clonidine, a structurally similar agent, in lowering blood pressure.', NULL, 'Caution is advised and more gradual dose titration may be appropriate when tizanidine is used with other substances that can cause CNS depression and/or hypotension, particularly in elderly or debilitated patients.', NULL, 'Caution is advised and more gradual dose titration may be appropriate when tizanidine is used with other substances that can cause CNS depression and/or hypotension, particularly in elderly or debilitated patients. Ambulatory patients should be counseled to avoid hazardous activities requiring complete mental alertness and motor coordination until they know how these agents affect them, and to avoid rising abruptly from a sitting or recumbent position. Patients should contact their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or excessive CNS effects that interfere with their normal activities.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/163476/', '[1] "Product Information. Zanaflex (tizanidine)." Acorda Therapeutics, Hawthorne, NY.[2] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[3] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[4] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[5] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (163304, 'Tofacitinib', 'Rufinamide', 'Moderate', 'Coadministration with moderate inducers of CYP450 3A4 may decrease the plasma concentrations of tofacitinib, which is primarily metabolized by the isoenzyme. The interaction may result in loss of or reduced clinical response; however, the extent to which these medications interact with tofacitinib has not been established.', NULL, 'Caution is recommended if tofacitinib is to be used in combination with moderate CYP450 3A4 inducers.', NULL, 'Caution is recommended if tofacitinib is to be used in combination with moderate CYP450 3A4 inducers. Alternative agents with no or minimal CYP450 3A4 induction potential are recommended when possible.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/163477/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Xeljanz (tofacitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767363025);
