INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421983, 'Flutamide', 'Rilpivirine', 'Moderate', 'Long-term androgen deprivation therapy can prolong the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', 'DDInter', 1767317476, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval. Electrolyte abnormalities should be corrected prior to initiating therapy, and periodic monitoring of electrocardiograms and electrolytes should be considered.', 'Synergism', 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421984, 'Fluvoxamine', 'Rilpivirine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of rilpivirine, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Caution and clinical monitoring may be advisable if CYP450 3A4 inhibitors are coadministered with rilpivirine.', 'Metabolism', 'Caution and clinical monitoring may be advisable if CYP450 3A4 inhibitors are coadministered with rilpivirine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421985, 'Fosaprepitant', 'Rilpivirine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of rilpivirine, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Caution and clinical monitoring may be advisable if CYP450 3A4 inhibitors are coadministered with rilpivirine.', 'Metabolism', 'Caution and clinical monitoring may be advisable if CYP450 3A4 inhibitors are coadministered with rilpivirine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421986, 'Fosphenytoin', 'Rilpivirine', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of rilpivirine, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of rilpivirine with potent CYP450 3A4 inducers is considered contraindicated.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, concomitant use of rilpivirine with potent CYP450 3A4 inducers is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421987, 'Fostemsavir', 'Rilpivirine', 'Moderate', 'Fostemsavir can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317476, 'Caution is recommended if fostemsavir is used in combination with other drugs that can prolong the QT interval. Consider obtaining electrocardiogram and serum electrolyte levels prior to initiating fostemsavir therapy and periodically thereafter, particularly in patients with risk factors for torsade de pointes and/or receiving one or more potent CYP450 3A4 inhibitors (e.g., azole antifungal agents; cobicistat; protease inhibitors; ketolide and certain macrolide antibiotics; ceritinib; idelalisib; nefazodone) or P-gp inhibitors (e.g., cyclosporine; diltiazem; quinidine; quinine; tacrolimus; verapamil). Correct hypokalemia and/or hypomagnesemia before starting treatment and as indicated during treatment, as they may be risk factors for ventricular arrhythmias.', 'Synergism', 'Caution is recommended if fostemsavir is used in combination with other drugs that can prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421988, 'Galantamine', 'Rilpivirine', 'Moderate', 'Acetylcholinesterase inhibitors may have vagotonic effects on the sinoatrial and atrioventricular nodes, which occasionally manifest as bradycardia or heart block (<2%). Because bradycardia is a risk factor for torsade de pointes, a theoretical risk exists when combined with agents that prolong the QT interval.', 'DDInter', 1767317476, 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval. Patients should be monitored for bradycardia, atrioventricular block and syncope, and advised to seek medical attention if they experience dizziness, lightheadedness, fainting, shortness of breath, or slow or irregular heartbeat.', 'Synergism', 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421989, 'Gatifloxacin', 'Rilpivirine', 'Major', 'Certain quinolones, including gatifloxacin and moxifloxacin, may cause dose-related prolongation of the QT interval in some patients. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', 'DDInter', 1767317476, 'Coadministration of gatifloxacin or moxifloxacin with other drugs that can prolong the QT interval should generally be avoided. Caution and clinical monitoring are recommended if concomitant use is required. Since the magnitude of QTc prolongation increases with increasing plasma concentrations of the quinolone, recommended dosages and intravenous infusion rates should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Coadministration of gatifloxacin or moxifloxacin with other drugs that can prolong the QT interval should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421990, 'Gemifloxacin', 'Rilpivirine', 'Moderate', 'Certain quinolones, including gemifloxacin, may cause dose-related prolongation of the QT interval in some patients. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317476, 'Although the risk of a serious interaction is probably low, caution is recommended if gemifloxacin is used in combination with other drugs that can prolong the QT interval. Since the magnitude of QTc prolongation may increase with increasing plasma concentrations of gemifloxacin, the recommended dosage should not be exceeded, especially in patients with renal or hepatic impairment.', 'Synergism', 'Although the risk of a serious interaction is probably low, caution is recommended if gemifloxacin is used in combination with other drugs that can prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421991, 'Gilteritinib', 'Rilpivirine', 'Moderate', 'Gilteritinib may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317476, 'Caution and clinical monitoring are recommended if gilteritinib is used in combination with other drugs that can prolong the QT interval. Patients should have electrocardiograms (ECGs) performed prior to initiation of treatment with gilteritinib, on days 8 and 15 of cycle 1, and prior to the start of the next two subsequent cycles. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution and clinical monitoring are recommended if gilteritinib is used in combination with other drugs that can prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1421992, 'Glycerin', 'Rilpivirine', 'Moderate', 'Bowel cleansing as well as overuse of certain laxatives may cause electrolyte loss and increase the risk of torsade de pointes ventricular arrhythmia in patients treated with drugs that prolong the QT interval. Electrolyte disturbances including hypokalemia and hypomagnesemia have been reported with laxative abuse and are known risk factors for torsade de pointes associated with QT interval prolongation.', 'DDInter', 1767317476, 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives. The recommended dosage and duration of use should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Patients treated with drugs that prolong the QT interval should exercise caution when self-medicating with laxatives.', NULL, NULL, 'Moderate', 'pharmacodynamic');
