INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1422044, 'Rilpivirine', 'Mefloquine', 'Moderate', 'Mefloquine is a myocardial depressant and can cause ECG abnormalities. Theoretically, coadministration with other agents that can affect cardiac conduction (e.g., antiarrhythmic agents, beta blockers, calcium channel blockers, certain antihistamines, tricyclic antidepressants, phenothiazines, some neuroleptics) may result in additive effects and increased risk of ventricular arrhythmias including ventricular tachycardia and torsade de pointes.', 'DDInter', 1767317476, 'Caution and clinical monitoring are recommended if mefloquine is used in combination with other drugs that can prolong the QT interval or otherwise affect cardiac conduction. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes or other arrhythmia such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution and clinical monitoring are recommended if mefloquine is used in combination with other drugs that can prolong the QT interval or otherwise affect cardiac conduction.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1422045, 'Rilpivirine', 'Methylphenobarbital', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of rilpivirine, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317476, 'To minimize the risk of reduced viral susceptibility and resistance development associated with subtherapeutic levels of rilpivirine, caution and clinical monitoring may be advisable if CYP450 3A4 inducers are coadministered with rilpivirine.', 'Metabolism', 'To minimize the risk of reduced viral susceptibility and resistance development associated with subtherapeutic levels of rilpivirine, caution and clinical monitoring may be advisable if CYP450 3A4 inducers are coadministered with rilpivirine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1422046, 'Rilpivirine', 'Mesoridazine', 'Major', 'Mesoridazine can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Mesoridazine overdosage has been associated with ventricular arrhythmias and death. The extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s). In addition, certain agents with anticholinergic properties (e.g., sedating antihistamines; antispasmodics; neuroleptics; phenothiazines; skeletal muscle relaxants; tricyclic antidepressants; disopyramide) may have additive parasympatholytic and central nervous system-depressant effects when used in combination with mesoridazine. Excessive parasympatholytic effects may include paralytic ileus, hyperthermia, mydriasis, blurred vision, tachycardia, urinary retention, psychosis, and seizures.', 'DDInter', 1767317476, 'Coadministration of mesoridazine with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', 'Coadministration of mesoridazine with other drugs that can prolong the QT interval is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1422047, 'Rilpivirine', 'Orciprenaline', 'Moderate', 'Beta-2 adrenergic agonists can cause dose-related prolongation of the QT interval and potassium loss. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clinically significant prolongation of QT interval and hypokalemia occur infrequently when beta-2 agonists are inhaled at normally recommended dosages. However, these effects may be more common when the drugs are administered systemically or when recommended dosages are exceeded.', 'DDInter', 1767317476, 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
