INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (163744, 'Xylometazoline (nasal)', 'Safinamide', 'Moderate', 'Monoamine oxidase inhibitors (MAOIs) may theoretically potentiate the cardiovascular effects of sympathomimetic agents following oral and/or topical administration (e.g., nasal decongestants, ophthalmic vasoconstrictors) by inhibiting the breakdown of catecholamines. There have been occasional reports of increased blood pressure and heart rate in patients receiving systemic direct-acting agents and MAOIs.', NULL, 'Caution and more frequent blood pressure monitoring may be advisable when oral and/or topical sympathomimetic agents are coadministered with MAOIs including safinamide.', NULL, 'Caution and more frequent blood pressure monitoring may be advisable when oral and/or topical sympathomimetic agents are coadministered with MAOIs including safinamide.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/163917/', '[1] Cusson JR, Goldenberg E, Larochelle P "Effect of a novel monoamine-oxidase inhibitor, moclobemide on the sensitivity to intravenous tyramine and norepinephrine in humans." J Clin Pharmacol 31 (1991): 462-7[2] "Product Information. Xadago (safinamide)." US WorldMeds LLC, Louisville , KY.[3] Darcy PF, Griffin JP "Interactions with drugs used in the treatment of depressive illness." Adverse Drug React Toxicol Rev 14 (1995): 211-31[4] Thompson DS, Sweet RA, Marzula K, Peredes JC "Lack of interaction of monoamine oxidase inhibitors and epinephrine in an older patient." J Clin Psychopharmacol 17 (1997): 322-3[5] Goulet JP, Perusse R, Turcotte JY "Contraindications to vasoconstrictors in dentistry: Part III. Pharmacolog