INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (163812, 'Indapamide', 'Salmeterol', 'Moderate', 'Coadministration with beta-2 adrenergic agonists may potentiate the hypokalemic effects of potassium-wasting diuretics. Beta-2 agonists can cause clinically significant but usually transient decreases in serum potassium concentrations. Since QT prolongation is a possible side effect of beta-2 agonists, exacerbation of hypokalemia may increase the risk of torsade de pointes and other serious arrhythmias. The interaction may be more likely with systemic or nebulized formulations of beta-2 agonists, high dosages of inhaled beta-2 agonists, or concomitant theophylline or corticosteroid therapy.', NULL, 'Caution is advised when beta-2 agonists are used with potassium-wasting diuretics.', NULL, 'Caution is advised when beta-2 agonists are used with potassium-wasting diuretics. Serum potassium level and cardiovascular status should be monitored, especially if the beta-2 agonist is administered systemically or by nebulizer. Patients should be advised to notify their physician if they experience potential signs and symptoms of hypokalemia such as fatigue, weakness, myalgia, muscle cramps, numbness, tingling, abdominal pain, constipation, palpitation, and irregular heartbeat.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/163985/', '[1] "Product Information. Proventil (albuterol)." Schering Laboratories, Kenilworth, NJ.[2] "Product Information. Combivent (albuterol-ipratropium)." Boehringer-Ingelheim, Ridgefield, CT.[3] Lipworth BJ, McDevitt DG, Struthers AD "Prior treatment with diuretic augments the hypokalemic and electrocardiographic effects of inhaled albuterol." Am J Med 86 (1989): 653-7[4] "Product Information. Striverdi Respimat (olodaterol)." Boehringer Ingelheim, Ridgefield, CT.[5] "Product Information. Brovana (arformoterol)." Sepracor Inc, Marlborough, MA.[6] "Product Information. Breo Ellipta (fluticasone-vilanterol)." GlaxoSmithKline, Research Triangle Park, NC.[7] "Product Information. Brethaire (terbutaline)." Novartis Pharmaceuticals, East Hanover, NJ.[8] "Product Information. Arcapta Neohaler (indacaterol)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (163813, 'Indinavir', 'Salmeterol', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the systemic levels and pharmacologic effects of salmeterol, which is primarily metabolized by the isoenzyme. Because salmeterol prolongs the QT interval in a dose-dependent manner, high systemic levels of salmeterol may increase the risk of ventricular arrhythmias such as ventricular tachycardia, ventricular fibrillation, and torsade de pointes.', NULL, 'Concomitant use of salmeterol with potent CYP450 3A4 inhibitors is not recommended.', NULL, 'Concomitant use of salmeterol with potent CYP450 3A4 inhibitors is not recommended. Some authorities recommend avoiding concomitant use of salmeterol during and for 2 weeks after treatment with itraconazole.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/163986/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[4] "Product Information. Severent (salmeterol)." Glaxo Wellcome, Research Triangle Park, NC.[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365208);
