INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (163960, 'Salsalate', 'Nalidixic acid', 'Moderate', 'Coadministration with nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of central nervous system toxicity sometimes associated with fluoroquinolone use. Some investigators suggest that the piperazine ring of fluoroquinolones may inhibit the binding of gamma-aminobutyric acid (GABA) to brain receptors and that NSAIDs may synergistically add to this effect. Patients with a history of seizures may be at greater risk.', NULL, 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', NULL, 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/164133/', '[1] "Product Information. Factive (gemifloxacin)." GeneSoft Inc, San Francisco, CA.[2] "Product Information. Avelox (moxifloxacin)" Bayer, West Haven, CT.[3] Davey PG "Overview of drug interactions with the quinolones." J Antimicrob Chemother 22(suppl c) (1988): 97-107[4] Domagala JM "Structure-activity and structure-side-effect relationships for the quinolone antibacterials." J Antimicrob Chemother 33 (1994): 685-706[5] "Product Information. Cipro (ciprofloxacin)." Bayer, West Haven, CT.[6] "Product Information. Floxin (ofloxacin)." Ortho Pharmaceutical Corporation, Raritan, NJ.[7] "Product Information. Tequin (gatifloxacin)" Bristol-Myers Squibb, Princeton, NJ.[8] Segev S. Rehavi M, Rubinstein E "Quinolones, theophylline, and diclofenac interactions with the gamma-aminobutyric acid receptor." Antimicrob Agents Chemother 32 (1988): 1624-6[9] Hooper DC, Wolfson JS "The fluoroquinolones: pharmacology, clinical uses, and toxicities in humans." Antimicrob Agents Chemother 28 (1985): 716-21[10] Ball P "Ciprofloxacin: an overview of adverse experiences." J Antimicrob Chemother 18 (1986): 187-93[11] "Product Information. Penetrex (enoxacin)." Rhone-Poulenc Rorer, Collegeville, PA.[12] "Product Information. Raxar (grepafloxacin)." Glaxo Wellcome, Research Triangle Park, NC.[13] Ball P, Tillotson G "Tolerability of fluoroquinolone antibiotics: past, present and future." Drug Saf 13 (1996): 343-8[14] "Product Information. Levaquin (levofloxacin)." Ortho Pharmaceutical Corporation, Raritan, NJ.', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (163961, 'Salsalate', 'Netilmicin', 'Moderate', 'The nephrotoxic effect of aminoglycosides may be potentiated by nonsteroidal anti-inflammatory drugs (NSAIDs), particularly if the latter had been given in high dosages for prolonged periods. Animal models suggest that renal prostaglandins may play a role in maintaining normal renal blood flow and glomerular filtration rate during the development of aminoglycoside nephrotoxicity, thus inhibition of their production by NSAIDs may worsen the renal damage. In premature infants, NSAIDs may increase the plasma concentrations of aminoglycosides. The proposed mechanism is decreased aminoglycoside clearance due to NSAID reduction of glomerular filtration rate.', NULL, 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy.', NULL, 'Whenever feasible, NSAID use should preferably be discontinued prior to initiating IV aminoglycoside therapy. If concomitant administration is necessary, hydration status as well as renal and vestibular functions should be closely monitored.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/164134/', '[1] Gagliardi L "Possible indomethacin-aminoglycoside interaction in preterm infants." J Pediatr 107 (1985): 991-2[2] Farag MM, Mikhail MR, Abdel-Meguid E, Abdel-Tawab S "Assessment of gentamicin-induced nephrotoxicity in rats treated with low doses of ibuprofen and diclofenac sodium." Clin Sci 91 (1996): 187-91[3] Scott CS, RetschBogart GZ, Henry MM "Renal failure and vestibular toxicity in an adolescent with cystic fibrosis receiving gentamicin and standard-dose ibuprofen." Pediat Pulm 31 (2001): 314-6[4] Kovesi TA, Swartz R, MacDonald N "Transient renal failure due to simultaneous ibuprofen and aminoglycoside therapy in children with cystic fibrosis." N Engl J Med 338 (1998): 65-6[5] Assael BM, Chiabrando C, Gagliardi L, Noseda A, Bamonte F, Salmona M "Prostaglandins and aminoglycoside nephrotoxicity." Toxicol Appl Pharmacol 78 (1985): 386-94[6] Zarfin Y, Koren G, Maresky D, et al "Clinical and laboratory observations: possible indomethacin-aminoglycoside interaction in preterm infants." J Pediatr 106 (1985): 511-3', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (163962, 'Salsalate', 'Penbutolol', 'Minor', 'High doses of salicylates may blunt the antihypertensive effects of beta-blockers. The proposed mechanism is inhibition of prostaglandin synthesis. Low-dose aspirin does not appear to affect blood pressure. In addition, beta-blockers may exert an antiplatelet effect, which may be additive with the effects of some salicylates. Metoprolol may also increase aspirin absorption and/or plasma concentrations of salicylates; however, the clinical significance of this effect is unknown. Data have been conflicting. Until more information is available, patients who require concomitant therapy should be monitored for altered antihypertensive response whenever a salicylate is introduced or discontinued, or when its dosage is modified.', NULL, '-', NULL, '-', 'Antagonism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/164135/', '[1] Keber I, Jerse M, Keber D, Stegnar M "The influence of combined treatment with propranolol and acetylsalicylic acid on platelet aggregation in coronary heart disease." Br J Clin Pharmacol 7 (1979): 287-91[2] Zanchetti A, Hansson L, Leonetti G, et al. "Low-dose aspirin does not interfere with the blood pressure-lowering effects of antihypertensive therapy." J Hypertens 20 (2002): 1015-1022[3] Sziegoleit W, Rausch J, Polak G, et al "Influence of acetylsalicylic acid on acute circulatory effects of the beta-blocking agents pindolol and propranolol in humans." Int J Clin Pharmacol Ther Toxicol 20 (1982): 423-30[4] Hartmann D, Stief G, Lingenfelder M, Guzelhan C, Horsch AK "Study on the possible interaction between tenoxicam and atenolol in hypertensive patients." Arzneimittelforschung 45-1 (1995): 494-8[5] Spahn H, Langguth P, Kirch W, et al "Pharmacokinetics of salicylates administered with metoprolol." Arzneimittelforschung 36 (1986): 1697-9[6] Sziegoleit W, Rausch J , Polak G, Gyorgy M, Dekov E, Bekes M "Influence of acetylsalicylic acid on acute circulatory effects of the beta-blocking agents pindolol and propranolol." Int J Clin Pharmacol Ther Toxicol 20 (1982): 423-30', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (163963, 'Salsalate', 'Prednisolone', 'Moderate', 'Coadministration with corticosteroids may decrease the serum concentrations and therapeutic effects of salicylates. Likewise, serum salicylate levels may increase following withdrawal of corticosteroid therapy, potentially resulting in salicylate toxicity. Pharmacologically, the potential for increased gastrointestinal (GI) toxicity, including inflammation, bleeding, ulceration and perforation, should be considered due to additive ulcerogenic effects of these agents (especially aspirin) on the GI mucosa.', NULL, 'Patients treated concomitantly with a corticosteroid may require higher dosages of salicylates or salicylate-like drugs.', NULL, 'Patients treated concomitantly with a corticosteroid may require higher dosages of salicylates or salicylate-like drugs. Pharmacologic response to these agents should be monitored more closely whenever a corticosteroid is added to or withdrawn from therapy in patients stabilized on their existing salicylate regimen, and the salicylate dosage adjusted as necessary. During concomitant therapy, patients should be advised to take the medications with food and to immediately report signs and symptoms of GI ulceration and bleeding such as severe abdominal pain, dizziness, lightheadedness, and the appearance of black, tarry stools. The selective use of prophylactic anti-ulcer therapy (e.g., antacids, H2-antagonists) may be appropriate, particularly in patients with a prior history of peptic ulcer disease or GI bleeding and in elderly and debilitated patients.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/164136/', '[1] Piper JM, Ray WA, Daugherty JR, Griffin MR "Corticosteroid use and peptic ulcer disease: role of nonsteroidal ani-inflammatory drugs." Ann Intern Med 114 (1991): 735-40[2] Hansen RA, Tu W, Wang J, Ambuehl R, McDonald CJ, Murray MD "Risk of adverse gastrointestinal events from inhaled corticosteroids." Pharmacotherapy 28 (2008): 1325-34[3] Koren G, Roifman C, Gelfand E, Lavi S, Suria D, Stein L "Corticosteroids-salicylate interaction in a case of juvenile rheumatoid arthritis." Ther Drug Monit 9 (1987): 177-9[4] Edelman J, Potter JM, Hackett LP "The effect of intra-articular steroids on plasma salicylate concentrations." Br J Clin Pharmacol 21 (1986): 301-7[5] Baer PA, Shore A, Ikeman RL "Transient fall in serum salicylate levels following intraarticular injection of steroid in patients with rheumatoid arthritis." Arthritis Rheum 30 (1987): 345-7', 1767365208);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (163964, 'Salsalate', 'Probenecid', 'Moderate', 'Large, anti-inflammatory doses of salicylates may inhibit the uricosuric effect of probenecid. The mechanism of interaction has not been established. In contrast, low, daily antiplatelet doses do not seem to interact.', NULL, 'Large doses of salicylates should be avoided in patients who require probenecid for the treatment of hyperuricemia or gout.', NULL, 'Large doses of salicylates should be avoided in patients who require probenecid for the treatment of hyperuricemia or gout. However, occasional use of low dosages for analgesia or daily use of cardioprotective dosages should generally not be a concern.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/164137/', '[1] Regal RE "Aspirin and uricosurics: interaction revisited." Drug Intell Clin Pharm 21 (1987): 219-20[2] Harris M, Bryant LR, Danaher P, Alloway J "Effect of low dose daily aspirin on serum urate levels and urinary excretion in patients receiving probenecid for gouty arthritis." J Rheumatol 27 (2000): 2873-6[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Diamond JS, Paolino JS "Evidence for a postsecretory reabsorptive site for uric acid in man." J Clin Invest 52 (1973): 1491-9[5] Goldbart A, Press J, Sofer S, Kapelushnik J "Near fatal acute colchicine intoxication in a child. A case report." Eur J Pediatr 159 (2000): 895-7[6] "Colchicine: serious interactions." Prescrire Int 17 (2008): 151-3[7] Wilbur K, Makowsky M "Colchicine myotoxicity: case reports and literature review." Pharmacotherapy 24 (2004): 1784-92[8] Schiff D, Drislane FW "Rapid-onset colch