    ) VALUES (1422104, 'Rilpivirine', 'Ranitidine (bismuth citrate)', 'Major', 'INTERVAL: Coadministration with H2-receptor antagonists may significantly decrease the oral bioavailability of rilpivirine. The proposed mechanism involves a pH-dependent reduction in dissolution and absorption of rilpivirine, which is poorly soluble over a wide pH range.', NULL, 'To minimize the risk of reduced viral susceptibility and resistance development associated with subtherapeutic levels of rilpivirine, H2-receptor antagonists should be administered at least 12 hours before or 4 hours after the rilpivirine dose.', NULL, 'To minimize the risk of reduced viral susceptibility and resistance development associated with subtherapeutic levels of rilpivirine, H2-receptor antagonists should be administered at least 12 hours before or 4 hours after the rilpivirine dose.', 'Absorption', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317476);
