INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (165297, 'Fosphenytoin', 'Sertraline', 'Moderate', 'Two case reports suggest that sertraline may significantly increase phenytoin levels when used concomitantly. The mechanism is thought to be related to inhibition of CYP450 2C9-mediated metabolism of phenytoin by sertraline. Other hydantoins may also be affected. In addition, coadministration of sertraline with potent inducers of CYP450 3A4, such as phenytoin, may result in a reduction in the plasma concentrations and therapeutic effects of sertraline. It cannot be excluded that other CYP450 3A4 inducers (e.g., carbamazepine, phenobarbital (phenobarbitone), rifampin, St. John''s wort) may also cause a reduction of sertraline plasma levels.', NULL, 'Clinical and laboratory monitoring for increased hydantoin levels and toxicity is recommended whenever sertraline is added or dosage is changed.', NULL, 'Clinical and laboratory monitoring for increased hydantoin levels and toxicity is recommended whenever sertraline is added or dosage is changed. Patients should be advised to notify their doctor if they experience symptoms of toxicity (e.g., drowsiness, visual disturbances, change in mental status, seizures, nausea, or ataxia). In addition, the potential for diminished therapeutic effects of sertraline should be considered when prescribed in combination with phenytoin. Patients should be closely monitored, and the dosage of sertraline adjusted as necessary. Data are not available to guide dosing of sertraline when coadministered with potent CYP450 3A4 inducers. Alternative agents with no or minimal CYP450 3A4-inducing activity may also be considered.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/165470/', '[1] "Product Information. Tegretol (carbamazepine)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Haselberger MB, Freedman LS, Tolbert S "Elevated serum phenytoin concentrations associated with coadministration of sertraline." J Clin Psychopharmacol 17 (1997): 107-9[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Ueda N, Yoshimura R, Umene-Nakano W, et al. "Grapefruit juice alters plasma sertraline levels after single ingestion of sertraline in healthy volunteers." World J Biol Psychiatry 10(4 Pt 3) (2009): 832-5[7] "Product Information. Zoloft (sertraline)." Roerig Division, New York, NY.[8] Lee AJ, Chan WK, Harralson AF, Buffum J, Bui BCC "The effects of grapefruit juice on sertraline metabolism: An in vitro and in vivo study." Clin Ther 21 (1999): 1890-9[9] Cerner Multum, Inc. "Australian Product Information." O 0[10] Ozuna J, Friel P "Effect of enteral tube feeding on serum phenytoin levels." J Neurosurg Nurs 16 (1984): 289-91[11] Fleisher D, Sheth N, Kou JH "Phenytoin interaction with enteral feedings administered through nasogastric tubes." JPEN J Parenter Enteral Nutr 14 (1990): 513-6[12] Krueger KA, Garnett WR, Comstock TJ, Fitzsimmons WE, Karnes HT, Pellock JM "Effect of two administration schedules of an enteral nutrient formula on phenytoin bioavailability." Epilepsia 28 (1987): 706-12[13] "Product Information. Dilantin (phenytoin)." Parke-Davis, Morris Plains, NJ.[14] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[15] Doak KK, Haas CE, Dunnigan KJ, et al. "Bioavailability of phenytoin acid and phenytoin sodium with enteral feedings." Pharmacotherapy 18 (1998): 637-45[16] Rodman DP, Stevenson TL, Ray TR "Phenytoin malabsorption after jejunostomy tube delivery." Pharmacotherapy 15 (1995): 801-5[17] Sellers EM, Holloway MR "Drug kinetics and alcohol ingestion." Clin Pharmacokinet 3 (1978): 440-52[18] Guidry JR, Eastwood TF, Curry SC "Phenytoin absorption in volunteers receiving selected enteral feedings." West J Med 150 (1989): 659-61[19] Marvel ME, Bertino JS "Comparative effects of an elemental and a complex enteral feeding formulation on the absorption of phenytoin suspension." JPEN J Parenter Enteral Nutr 15 (1991): 316-8[20] Holtz L, Milton J, Sturek JK "Compatibility of medications with enteral feedings." JPEN J Parenter Enteral Nutr 11 (1987): 183-6[21] Haley CJ, Nelson J "Phenytoin-enteral feeding interaction." DICP 23 (1989): 796-8[22] Faraji B, Yu PP "Serum phenytoin levels of patients on gastrostomy tube feeding." J Neurosci Nurs 30 (1998): 55-9[23] Au Yeung SC, Ensom MH "Phenytoin and enteral feedings: does evidence support an interaction?" Ann Pharmacother 34 (2000): 896-905[24] Sandor P, Sellers EM, Dumbrell M, Khouw V "Effect of short- and long-term alcohol use on phenytoin kinetics in chronic alcoholics." Clin Pharmacol Ther 30 (1981): 390-7', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (165298, 'Fostamatinib', 'Sertraline', 'Moderate', 'Coadministration with fostamatinib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme and/or P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine and/or liver due to inhibition of CYP450 3A4-mediated metabolism and P-gp-mediated efflux by fostamatinib.', NULL, 'Caution is advised when fostamatinib is used concurrently with drugs that are known substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when fostamatinib is used concurrently with drugs that are known substrates of CYP450 3A4 and/or P-gp, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fostamatinib is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/165471/', '[1] "Product Information. Tavalisse (fostamatinib)." Rigel Pharmaceuticals, South San Francisco, CA.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (165299, 'Fostemsavir', 'Sertraline', 'Moderate', 'Fostemsavir can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Caution is recommended if fostemsavir is used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if fostemsavir is used in combination with other drugs that can prolong the QT interval. Consider obtaining electrocardiogram and serum electrolyte levels prior to initiating fostemsavir therapy and periodically thereafter, particularly in patients with risk factors for torsade de pointes and/or receiving one or more potent CYP450 3A4 inhibitors (e.g., azole antifungal agents; cobicistat; protease inhibitors; ketolide and certain macrolide antibiotics; ceritinib; idelalisib; nefazodone) or P-gp inhibitors (e.g., cyclosporine; diltiazem; quinidine; quinine; tacrolimus; verapamil). Correct hypokalemia and/or hypomagnesemia before starting treatment and as indicated during treatment, as they may be risk factors for ventricular arrhythmias.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/165472/', '[1] "Product Information. Rukobia (fostemsavir)." ViiV Healthcare, Research Triangle Park, NC.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (165300, 'Frovatriptan', 'Sertraline', 'Major', 'Concomitant use of agents with serotonergic activity such as serotonin reuptake inhibitors, monoamine oxidase inhibitors, tricyclic antidepressants, 5-HT1 receptor agonists, ergot alkaloids, cyclobenzaprine, lithium, St. John''s wort, phenylpiperidine opioids, dextromethorphan, and tryptophan may potentiate the risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors. Symptoms of the serotonin syndrome may include mental status changes such as irritability, altered consciousness, confusion, hallucination, and coma; autonomic dysfunction such as tachycardia, hyperthermia, diaphoresis, shivering, blood pressure lability, and mydriasis; neuromuscular abnormalities such as hyperreflexia, myoclonus, tremor, rigidity, and ataxia; and gastrointestinal symptoms such as abdominal cramping, nausea, vomiting, and diarrhea.', NULL, 'In general, the concomitant use of multiple serotonergic agents should be avoided if possible, or otherwise approached with caution if potential benefit is deemed to outweigh the risk.', NULL, 'In general, the concomitant use of multiple serotonergic agents should be avoided if possible, or otherwise approached with caution if potential benefit is deemed to outweigh the risk. Patients should be closely monitored for symptoms of the serotonin syndrome during treatment. Particular caution is advised when increasing the dosages of these agents. The potential risk for serotonin syndrome should be considered even when administering serotonergic agents sequentially, as some agents may demonstrate 