INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1422694, 'Encorafenib', 'Sulfamethoxazole', 'Minor', 'Limited data suggest that sulfamethoxazole-trimethoprim (SMX-TMP) may rarely prolong the QT interval of the electrocardiogram. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. There have been isolated reports of QT prolongation and ventricular arrhythmias occurring in patients treated with SMX-TMP intravenously. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'DDInter', 1767317476, '-', 'Synergism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1422695, 'Encorafenib', 'Sulfasalazine', 'Moderate', 'Coadministration with encorafenib may theoretically increase the plasma concentrations of drugs that are substrates of P-glycoprotein (P-gp), breast cancer resistance protein (BCRP), organic cation transporter (OCT2), organic anion transporter (OAT1, OAT3), or organic anion transporting polypeptide (OATP1B1, OATP1B3). The proposed mechanism is decreased clearance due to inhibition of the corresponding transporter by encorafenib.', 'DDInter', 1767317476, 'Caution is advised if encorafenib must be used concomitantly with drugs that are substrates of the affected transporters, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever encorafenib is added to or withdrawn from therapy.', 'Excretion', 'Caution is advised if encorafenib must be used concomitantly with drugs that are substrates of the affected transporters, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1422696, 'Encorafenib', 'Sulfinpyrazone', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of encorafenib, which is primarily metabolized by the isoenzyme. The extent to which CYP450 3A4 inducers may affect encorafenib exposure has not been studied.', 'DDInter', 1767317476, 'The potential for diminished pharmacologic effects of encorafenib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments or a dose adjustment of encorafenib may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of encorafenib should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1422697, 'Encorafenib', 'Sunitinib', 'Moderate', 'Encorafenib may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317476, 'Coadministration of encorafenib with other drugs that can prolong the QT interval should generally be avoided. Caution and clinical monitoring are recommended if concomitant use is required. Hypokalemia and hypomagnesemia should be corrected prior to initiation of encorafenib treatment, and electrolytes periodically monitored during treatment. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. Withhold, reduce dose, or permanently discontinue encorafenib in accordance with the product labeling for QTc exceeding 500 msec.', 'Synergism', 'Coadministration of encorafenib with other drugs that can prolong the QT interval should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1422698, 'Encorafenib', 'Suvorexant', 'Moderate', 'Coadministration with encorafenib may alter the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4, resulting in increased toxicity or decreased efficacy of these drugs.', 'DDInter', 1767317476, 'Concomitant use of encorafenib and sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges should generally be avoided. These drugs include, but are not limited to: alfentanil, cisapride, colchicine, cyclosporine, fentanyl, ivacaftor, lovastatin, simvastatin, oral midazolam, triazolam, pimozide, quinidine, sildenafil, ergot derivatives, macrolide immunosuppressants, and vinca alkaloids.', 'Metabolism', 'Concomitant use of encorafenib and sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
