INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1422900, 'Oxycodone', 'Methoxyflurane', 'Moderate', 'Opioids may be expected to decrease the minimum alveolar concentration required for inhalation anesthetics. When these agents are used concomitantly, less inhalation anesthetic may be required.', 'DDInter', 1767317476, 'In general, anesthesia should be titrated to clinical effect by those experienced in anesthesia technique and in the care of patients receiving opioids.', 'Synergism', 'In general, anesthesia should be titrated to clinical effect by those experienced in anesthesia technique and in the care of patients receiving opioids.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1422901, 'Oxycodone', 'Methscopolamine', 'Moderate', 'Coadministration of opioids with anticholinergic agents may result in additive central nervous system (CNS), gastrointestinal, and genitourinary effects. The risk and/or severity of adverse effects such as sedation, dizziness, confusion, cognitive and psychomotor impairment, dry mouth, constipation, and urinary retention may increase. Severe constipation may lead to paralytic ileus in some cases.', 'DDInter', 1767317476, 'Caution and close monitoring of central nervous system, gastrointestinal, and genitourinary adverse effects are recommended when opioids are used with anticholinergic agents. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'Caution and close monitoring of central nervous system, gastrointestinal, and genitourinary adverse effects are recommended when opioids are used with anticholinergic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
