INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (165657, 'Sildenafil', 'Riociguat', 'Major', 'Coadministration of riociguat with phosphodiesterase (PDE) inhibitors may cause significant hypotension. The mechanism involves peripheral vasodilation secondary to enhanced levels of cyclic guanosine monophosphate (cGMP) in vascular smooth muscle cells, as PDE inhibitors prevent degradation of cGMP while riociguat promotes its synthesis by stimulating soluble guanylate cyclase (sGC), an enzyme in the cardiopulmonary system that binds with nitric oxide (NO) to catalyze the synthesis of cGMP. Riociguat does not affect the pharmacokinetics of sildenafil.', NULL, 'Concomitant use of riociguat with PDE inhibitors, including specific PDE-5 inhibitors (e.', NULL, 'Concomitant use of riociguat with PDE inhibitors, including specific PDE-5 inhibitors (e.g., avanafil, sildenafil, tadalafil, vardenafil) or nonspecific PDE inhibitors (e.g., dipyridamole, theophylline), is considered contraindicated. Riociguat should be discontinued at least 24 hours before administering a PDE-5 inhibitor, and avoidance of riociguat administration within 24 hours after sildenafil or within 48 hours after tadalafil is recommended. Limited data exists for other PDE inhibitors.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/165830/', '[1] "Product Information. Adempas (riociguat)." Bayer Pharmaceutical Inc, West Haven, CT.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Adempas (riociguat)." Bayer Pharmaceutical Inc, West Haven, CT.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (165658, 'Silodosin', 'Sildenafil', 'Moderate', 'Sildenafil may potentiate the hypotensive effect of alpha blockers, resulting in symptomatic hypotension in some patients. Sildenafil inhibits phosphodiesterase-5-mediated degradation of cyclic guanosine monophosphate (cGMP), which in vascular smooth muscles can cause peripheral vasodilation that may be additive with that induced by alpha blockers.', NULL, 'Caution is advised if sildenafil is used in combination with alpha blockers.', NULL, 'Caution is advised if sildenafil is used in combination with alpha blockers. Patients who demonstrate hemodynamic instability on alpha blocker therapy alone are at increased risk of symptomatic hypotension with concomitant use of sildenafil. Therefore, patients should be stable on alpha blocker therapy prior to initiating sildenafil, and sildenafil should be initiated at the lowest recommended dosage when used for erectile dysfunction (i.e., 25 mg). No dosage adjustment is necessary if used in the treatment of pulmonary arterial hypertension. In those patients already on an optimized dosage of sildenafil, alpha blocker therapy should be initiated at the lowest dosage. The safety of sildenafil in combination with alpha blockers may be affected by other variables such as intravascular volume depletion and use of other antihypertensive medications.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/165831/', '[1] "Product Information. Viagra (sildenafil)." Pfizer US Pharmaceuticals, New York, NY.[2] "Product Information. Rapaflo (silodosin)." Watson Pharmaceuticals, Corona, CA.[3] "Product Information. Revatio (sildenafil)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[4] Jetter A, Kinzig-Schippers M, Walchner-Bonjean M, et al. "Effects of grapefruit juice on the pharmacokinetics of sildenafil." Clin Pharmacol Ther 71 (2002): 21-29', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (165659, 'Simeprevir', 'Sildenafil', 'Moderate', 'Coadministration with simeprevir may result in mild increases in plasma concentrations of phosphodiesterase-5 (PDE5) inhibitors, which are primarily metabolized by CYP450 3A4. In vivo, simeprevir mildly inhibits intestinal CYP450 3A4, but does not affect hepatic CYP450 3A4 activity. Therefore, a major pharmacokinetic interaction is not expected under normal circumstances.', NULL, 'When prescribed with simeprevir, no empiric dosage adjustment is required for PDE5 inhibitors in the treatment of erectile dysfunction.', NULL, 'When prescribed with simeprevir, no empiric dosage adjustment is required for PDE5 inhibitors in the treatment of erectile dysfunction. However, dosage adjustment of sildenafil or tadalafil may be needed when these agents are administered chronically for the treatment of pulmonary arterial hypertension. Consider starting with the lowest effective dose and increase as needed, with clinical monitoring as appropriate.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/165832/', '[1] "Product Information. Olysio (simeprevir)." Janssen Pharmaceuticals, Titusville, NJ.[2] "Product Information. Cialis (tadalafil)." Lilly, Eli and Company, Indianapolis, IN.[3] "Product Information. Adcirca (tadalafil)." United Therapeutics Corporation, Silver Spring, MD.[4] "Product Information. Olysio (simeprevir)." Janssen Pharmaceuticals, Titusville, NJ.', 1767363025);
