INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (165878, 'Cerivastatin', 'Simeprevir', 'Moderate', 'Coadministration with simeprevir may increase the plasma concentrations of various statins. The proposed mechanism is simeprevir inhibition of OATP1B1-mediated hepatic uptake and/or CYP450 3A4-mediated intestinal metabolism of the affected statins.', NULL, 'The lowest dosage of affected statins should be used when prescribed in combination with simeprevir, and the dosage titrated cautiously.', NULL, 'The lowest dosage of affected statins should be used when prescribed in combination with simeprevir, and the dosage titrated cautiously. Alternatively, fluvastatin may be substituted, as it has been shown to have no significant interaction with simeprevir. All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/166051/', '[1] "Product Information. Olysio (simeprevir)." Janssen Pharmaceuticals, Titusville, NJ.[2] Richter WO, Jacob BG, Schwandt P "Interaction between fibre and lovastatin." Lancet 338 (1991): 706[3] Neuvonen PJ, Backman JT, Niemi M "Pharmacokinetic comparison of the potential over-the-counter statins simvastatin, lovastatin, fluvastatin and pravastatin." Clin Pharmacokinet 47 (2008): 463-74[4] Lilja JJ, Kivisto KT, Neuvonen PJ "Grapefruit juice-simvastatin interaction: Effect on