INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (165887, 'Felodipine', 'Simeprevir', 'Moderate', 'Coadministration with simeprevir may increase the plasma concentrations and pharmacologic effects of calcium channel blockers, especially the dihydropyridines (e.g., amlodipine, felodipine, nicardipine, nifedipine, nisoldipine). The mechanism involves simeprevir inhibition of CYP450 3A4-mediated metabolism and P-glycoprotein-mediated efflux in the intestine. The interaction is not expected to occur with intravenously administered agents, since simeprevir does not affect hepatic CYP450 3A4 activity.', NULL, 'Close monitoring of clinical response and tolerance is recommended if calcium channel blockers are used in combination with simeprevir.', NULL, 'Close monitoring of clinical response and tolerance is recommended if calcium channel blockers are used in combination with simeprevir. Dosage reduction may be required for the calcium channel blocker, particularly if it is a dihydropyridine. Patients should be advised to seek medical attention if they experience edema or swelling of the lower extremities; sudden, unexplained weight gain; difficulty breathing; chest pain or tightness; or hypotension as indicated by dizziness, fainting, or orthostasis.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/166060/', '[1] "Product Information. Olysio (simeprevir)." Janssen Pharmaceuticals, Titusville, NJ.[2] "Grapefruit juice interactions with drugs." Med Lett Drugs Ther 37 (1995): 73-4[3] Bailey DG, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit juice felodipine interaction: Effect of naringin and 6'',7''-dihydroxybergamottin in humans." Clin P