INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (165898, 'Simeprevir', 'Quinidine', 'Moderate', 'Coadministration with simeprevir may increase the plasma concentrations of certain antiarrhythmic agents that are metabolized by CYP450 1A2 or 3A4 such as amiodarone, disopyramide, flecainide, mexiletine, propafenone, and quinidine. In vivo, simeprevir mildly inhibits CYP450 1A2 and intestinal CYP450 3A4, but does not affect hepatic CYP450 3A4 activity. Serious and/or life-threatening reactions including cardiac arrhythmias like torsade de pointes may occur if antiarrhythmic drug levels are significantly increased.', NULL, 'Caution is advised if simeprevir is prescribed in combination with antiarrhythmic agents that are metabolized by CYP450 1A2 or 3A4.', NULL, 'Caution is advised if simeprevir is prescribed in combination with antiarrhythmic agents that are metabolized by CYP450 1A2 or 3A4. Pharmacologic response and plasma antiarrhythmic drug levels should be monitored more closely whenever simeprevir is added to or withdrawn from therapy, and the antiarrhythmic dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/166071/', '[1] "Product Information. Olysio (simeprevir)." Janssen Pharmaceuticals, Titusville, NJ.[2] Ace LN, Jaffe JM, Kunka RL "Effect of food and an antacid on quinidine bioavailability." Biopharm Drug Dispos 4 (1983): 183-90[3] Min DI, Ku YM, Geraets DR, Lee HC "Effect of grapefruit juice on the pharmacokinetics and pharmacodynamics of quinidine in healthy volunteers." J Clin Pharmacol 36 (1996): 469-76[4] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77[5] Ha HR, Chen J, Leuenberger PM, Freiburghaus AU, Follah F "In vitro inhibition of midazolam and quinidine metabolism by flavonoids." Eur J Clin Pharmacol 48 (1995): 367-71[6] "Product Information. Olysio (simeprevir)." Janssen Pharmaceuticals, Titusville, NJ.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (165899, 'Simeprevir', 'Relugolix', 'Major', 'Coadministration with inhibitors of the P-glycoprotein (P-gp) efflux transporter may increase the plasma concentrations of relugolix, particularly when the inhibitors are given orally. Relugolix is a substrate for intestinal P-gp.', NULL, 'Concomitant use of relugolix with orally administered P-gp inhibitors should generally be avoided.', NULL, 'Concomitant use of relugolix with orally administered P-gp inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends taking relugolix first and separating the dosing by at least 6 hours. Patients should be monitored more frequently for adverse effects. Alternatively, treatment with relugolix may be interrupted for up to two weeks if a short course of treatment with a P-gp inhibitor is necessary.', 'Absorption', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/166072/', '[1] "Product Information. Orgovyx (relugolix)." Myovant Sciences, Inc., Brisbane, CA.[2] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (165900, 'Simvastatin', 'Simeprevir', 'Moderate', 'Coadministration with simeprevir may increase the plasma concentrations of various statins. The proposed mechanism is simeprevir inhibition of OATP1B1-mediated hepatic uptake and/or CYP450 3A4-mediated intestinal metabolism of the affected statins.', NULL, 'The lowest dosage of affected statins should be used when prescribed in combination with simeprevir, and the dosage titrated cautiously.', NULL, 'The lowest dosage of affected statins should be used when prescribed in combination with simeprevir, and the dosage titrated cautiously. Alternatively, fluvastatin may be substituted, as it has been shown to have no significant interaction with simeprevir. All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/166073/', '[1] "Product Information. Olysio (simeprevir)." Janssen Pharmaceuticals, Titusville, NJ.[2] Richter WO, Jacob BG, Schwandt P "Interaction between fibre and lovastatin." Lancet 338 (1991): 706[3] Neuvonen PJ, Backman JT, Niemi M "Pharmacokinetic comparison of the potential over-the-counter statins simvastatin, lovastatin, fluvastatin and pravastatin." Clin Pharmacokinet 47 (2008): 463-74[4] Lilja JJ, Kivisto KT, Neuvonen PJ "Grapefruit juice-simvastatin interaction: Effect on 