INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (165902, 'Tacrolimus', 'Simeprevir', 'Moderate', 'Coadministration with simeprevir may affect the pharmacokinetics of immunosuppressants that are substrates of the P-glycoprotein efflux transporter and/or CYP450 3A4 isoenzyme. In vivo, simeprevir mildly inhibits P-glycoprotein and intestinal CYP450 3A4, but does not affect hepatic CYP450 3A4 activity.', NULL, 'No empiric dosage adjustment is required for everolimus, sirolimus, or tacrolimus when prescribed with simeprevir.', NULL, 'No empiric dosage adjustment is required for everolimus, sirolimus, or tacrolimus when prescribed with simeprevir. However, because these agents have a narrow therapeutic index, it may be appropriate to monitor pharmacologic response and drug levels more closely following initiation or discontinuation of simeprevir, and the dosage adjusted if necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/166075/', '[1] "Product Information. Olysio (simeprevir)." Janssen Pharmaceuticals, Titusville, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Hooks MA "Tacrolimus, a new immunosuppressant--a review of the literature." Ann Pharmacother 28 (1994): 501-11[5] "Product Information. Prograf (tacrolimus)." Fujisawa, Deerfield, IL.[6] "Product Information. Olysio (simeprevir)." Janssen Pharmaceuticals, Titusville, NJ.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (165903, 'Tadalafil', 'Simeprevir', 'Moderate', 'Coadministration with simeprevir may result in mild increases in plasma concentrations of phosphodiesterase-5 (PDE5) inhibitors, which are primarily metabolized by CYP450 3A4. In vivo, simeprevir mildly inhibits intestinal CYP450 3A4, but does not affect hepatic CYP450 3A4 activity. Therefore, a major pharmacokinetic interaction is not expected under normal circumstances.', NULL, 'When prescribed with simeprevir, no empiric dosage adjustment is required for PDE5 inhibitors in the treatment of erectile dysfunction.', NULL, 'When prescribed with simeprevir, no empiric dosage adjustment is required for PDE5 inhibitors in the treatment of erectile dysfunction. However, dosage adjustment of sildenafil or tadalafil may be needed when these agents are administered chronically for the treatment of pulmonary arterial hypertension. Consider starting with the lowest effective dose and increase as needed, with clinical monitoring as appropriate.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/166076/', '[1] "Product Information. Olysio (simeprevir)." Janssen Pharmaceuticals, Titusville, NJ.[2] "Product Information. Cialis (tadalafil)." Lilly, Eli and Company, Indianapolis, IN.[3] "Product Information. Adcirca (tadalafil)." United Therapeutics Corporation, Silver Spring, MD.[4] "Product Information. Olysio (simeprevir)." Janssen Pharmaceuticals, Titusville, NJ.', 1767363025);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (165904, 'Talazoparib', 'Simeprevir', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may increase the plasma concentrations of talazoparib, which has been shown in vitro to be a substrate of both efflux membrane transporters.', NULL, 'No initial dosage adjustment is recommended by the manufacturer when talazoparib is coadministered with inhibitors of BCRP and/or P-gp other than amiodarone, carvedilol, clarithromycin, itraconazole, or verapamil.', NULL, 'No initial dosage adjustment is recommended by the manufacturer when talazoparib is coadministered with inhibitors of BCRP and/or P-gp other than amiodarone, carvedilol, clarithromycin, itraconazole, or verapamil. However, patients should be closely monitored for adverse effects such as myelosuppression and myelodysplastic syndrome/acute myeloid leukemia, and dosage adjustments made or treatment withheld as needed in accordance with the product labeling.', 'Distribution', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/166077/', '[1] "Product Information. Talzenna (talazoparib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767363025);
